import React, { useState, useEffect } from 'react';
import { motion, useAnimation } from 'framer-motion';
import HeroBackground from './HeroBackground';

const Hero = () => {
  const [mousePosition, setMousePosition] = useState({ x: 0, y: 0 });
  const controls = useAnimation();

  useEffect(() => {
    const handleMouseMove = (e) => {
      setMousePosition({
        x: (e.clientX / window.innerWidth) * 100,
        y: (e.clientY / window.innerHeight) * 100,
      });
    };

    window.addEventListener('mousemove', handleMouseMove);
    return () => window.removeEventListener('mousemove', handleMouseMove);
  }, []);

  // Animated title text
  const titleText = "Creative Developer";
  const subtitleText = "Hi, I am Rabeya Khatun, a MERN Stack Developer";

  const containerVariants = {
    hidden: { opacity: 0 },
    visible: {
      opacity: 1,
      transition: {
        delayChildren: 0.5,
        staggerChildren: 0.1
      }
    }
  };

  const letterVariants = {
    hidden: { 
      opacity: 0, 
      y: 50,
      rotateX: -90,
      scale: 0.8
    },
    visible: {
      opacity: 1,
      y: 0,
      rotateX: 0,
      scale: 1,
      transition: {
        duration: 0.8,
        ease: [0.6, 0.01, -0.05, 0.95]
      }
    }
  };

  const floatingVariants = {
    animate: {
      y: [-10, 10, -10],
      rotate: [-2, 2, -2],
      transition: {
        duration: 6,
        repeat: Infinity,
        ease: "easeInOut"
      }
    }
  };

  const buttonVariants = {
    initial: { scale: 1 },
    hover: {
      scale: 1.05,
      boxShadow: "0 20px 40px rgba(20, 184, 166, 0.3)",
      transition: {
        duration: 0.3,
        ease: "easeOut"
      }
    },
    tap: { scale: 0.95 }
  };

  return (
    <section id="home" className="relative min-h-screen overflow-hidden pt-20">
      {/* Dynamic Background */}
      <div className="absolute inset-0 bg-gradient-to-br from-slate-900 via-teal-900 to-emerald-900">
        {/* Animated Gradient Mesh */}
        <motion.div
          className="absolute inset-0 opacity-30"
          style={{
            background: `radial-gradient(circle at ${mousePosition.x}% ${mousePosition.y}%, 
              rgba(20, 184, 166, 0.3) 0%, 
              rgba(6, 182, 212, 0.2) 25%, 
              rgba(249, 115, 22, 0.1) 50%, 
              transparent 70%)`
          }}
        />
        
        {/* Floating Abstract Shapes */}
        <div className="absolute inset-0">
          {[...Array(12)].map((_, i) => (
            <motion.div
              key={i}
              className="absolute rounded-full opacity-20"
              style={{
                left: `${10 + (i * 8)}%`,
                top: `${15 + (i % 4) * 20}%`,
                width: `${60 + (i % 3) * 40}px`,
                height: `${60 + (i % 3) * 40}px`,
                background: i % 3 === 0 
                  ? 'linear-gradient(135deg, rgba(20, 184, 166, 0.3), rgba(6, 182, 212, 0.1))'
                  : i % 3 === 1
                  ? 'linear-gradient(135deg, rgba(249, 115, 22, 0.3), rgba(234, 88, 12, 0.1))'
                  : 'linear-gradient(135deg, rgba(6, 182, 212, 0.3), rgba(8, 145, 178, 0.1))'
              }}
              animate={{
                y: [-20, 20, -20],
                x: [-10, 10, -10],
                rotate: [0, 180, 360],
                scale: [1, 1.1, 1],
              }}
              transition={{
                duration: 8 + i * 0.5,
                repeat: Infinity,
                ease: "easeInOut",
                delay: i * 0.3,
              }}
            />
          ))}
        </div>

        {/* Wave Pattern Overlay */}
        <svg className="absolute bottom-0 left-0 w-full h-32" viewBox="0 0 1200 120" preserveAspectRatio="none">
          <motion.path
            d="M0,60 C300,120 900,0 1200,60 L1200,120 L0,120 Z"
            fill="rgba(20, 184, 166, 0.1)"
            animate={{
              d: [
                "M0,60 C300,120 900,0 1200,60 L1200,120 L0,120 Z",
                "M0,80 C300,140 900,20 1200,80 L1200,120 L0,120 Z",
                "M0,40 C300,100 900,-20 1200,40 L1200,120 L0,120 Z",
                "M0,60 C300,120 900,0 1200,60 L1200,120 L0,120 Z"
              ]
            }}
            transition={{
              duration: 12,
              repeat: Infinity,
              ease: "easeInOut"
            }}
          />
        </svg>
      </div>

      {/* Main Content */}
      <div className="relative z-10 max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 min-h-screen flex items-center">
        <div className="grid grid-cols-1 lg:grid-cols-2 gap-12 items-center w-full">
          
          {/* Left Column - Text Content */}
          <motion.div
            className="text-left"
            variants={containerVariants}
            initial="hidden"
            animate="visible"
          >
            {/* Animated Main Title */}
            <div className="mb-6 overflow-hidden">
              <motion.div className="flex flex-wrap">
                {titleText.split('').map((letter, index) => (
                  <motion.span
                    key={index}
                    className="text-5xl md:text-7xl lg:text-8xl font-bold bg-gradient-to-r from-white via-cyan-200 to-teal-300 bg-clip-text text-transparent inline-block"
                    variants={letterVariants}
                    style={{
                      textShadow: '0 0 30px rgba(20, 184, 166, 0.5)'
                    }}
                    whileHover={{
                      scale: 1.1,
                      rotate: [0, -5, 5, 0],
                      transition: { duration: 0.3 }
                    }}
                  >
                    {letter === ' ' ? '\u00A0' : letter}
                  </motion.span>
                ))}
              </motion.div>
            </div>

            {/* Subtitle with Wave Animation */}
            <motion.div
              className="mb-8 overflow-hidden"
              initial={{ opacity: 0, y: 30 }}
              animate={{ opacity: 1, y: 0 }}
              transition={{ duration: 0.8, delay: 1.5 }}
            >
              <motion.p 
                className="text-xl md:text-2xl text-cyan-100 font-medium leading-relaxed"
                animate={{
                  backgroundPosition: ['0% 50%', '100% 50%', '0% 50%'],
                }}
                transition={{
                  duration: 4,
                  repeat: Infinity,
                  ease: "easeInOut"
                }}
                style={{
                  background: 'linear-gradient(90deg, rgba(255,255,255,0.9), rgba(6,182,212,0.8), rgba(20,184,166,0.9), rgba(255,255,255,0.9))',
                  backgroundSize: '200% 100%',
                  WebkitBackgroundClip: 'text',
                  WebkitTextFillColor: 'transparent',
                  backgroundClip: 'text'
                }}
              >
                {subtitleText}
              </motion.p>
            </motion.div>

            {/* CTA Button */}
            <motion.div
              initial={{ opacity: 0, y: 30 }}
              animate={{ opacity: 1, y: 0 }}
              transition={{ duration: 0.8, delay: 2 }}
            >
              <motion.button
                className="relative px-8 py-4 bg-gradient-to-r from-teal-500 to-cyan-500 text-white font-semibold rounded-full overflow-hidden group"
                variants={buttonVariants}
                initial="initial"
                whileHover="hover"
                whileTap="tap"
              >
                <span className="relative z-10 text-lg">View My Work</span>
                
                {/* Animated Border */}
                <motion.div
                  className="absolute inset-0 bg-gradient-to-r from-coral-500 to-orange-500 opacity-0 group-hover:opacity-100"
                  transition={{ duration: 0.3 }}
                />
                
                {/* Wave Effect */}
                <motion.div
                  className="absolute inset-0 bg-white opacity-0"
                  whileHover={{
                    opacity: [0, 0.1, 0],
                    scale: [1, 1.05, 1],
                  }}
                  transition={{ duration: 0.6 }}
                />
                
                {/* Ripple Effect */}
                <motion.div
                  className="absolute -inset-2 bg-gradient-to-r from-teal-400 to-cyan-400 rounded-full opacity-30 blur-lg"
                  animate={{
                    scale: [1, 1.1, 1],
                    opacity: [0.3, 0.5, 0.3],
                  }}
                  transition={{
                    duration: 3,
                    repeat: Infinity,
                    ease: "easeInOut"
                  }}
                />
              </motion.button>
            </motion.div>

            {/* Floating Decorative Elements */}
            <div className="absolute -left-10 top-20 opacity-30">
              <motion.div
                className="w-20 h-20 border-2 border-cyan-400 rounded-full"
                variants={floatingVariants}
                animate="animate"
              />
            </div>
            <div className="absolute -left-5 bottom-20 opacity-20">
              <motion.div
                className="w-12 h-12 bg-gradient-to-r from-coral-400 to-orange-400 rounded-lg"
                variants={floatingVariants}
                animate="animate"
                style={{ animationDelay: '1s' }}
              />
            </div>
          </motion.div>

          {/* Right Column - Profile Image */}
          <motion.div
            className="flex justify-center lg:justify-end"
            initial={{ opacity: 0, scale: 0.8, x: 100 }}
            animate={{ opacity: 1, scale: 1, x: 0 }}
            transition={{ duration: 1, delay: 1, ease: "easeOut" }}
          >
            <div className="relative">
              {/* Main Image Container */}
              <motion.div
                className="relative w-80 h-80 md:w-96 md:h-96"
                variants={floatingVariants}
                animate="animate"
              >
                {/* Blob Shape Background */}
                <div className="absolute inset-0 bg-gradient-to-br from-teal-400 via-cyan-400 to-blue-500 rounded-full opacity-20 blur-xl" />
                
                {/* Glass Morphism Frame */}
                <div className="absolute inset-4 bg-white/10 backdrop-blur-md rounded-3xl border border-white/20 overflow-hidden shadow-2xl">
                  <img 
                    src="https://via.placeholder.com/400x400/0ea5e9/ffffff?text=RK" 
                    alt="Rabeya Khatun" 
                    className="w-full h-full object-cover"
                  />
                  
                  {/* Overlay Gradient */}
                  <div className="absolute inset-0 bg-gradient-to-t from-teal-900/20 via-transparent to-cyan-900/20" />
                </div>

                {/* Floating Particles Around Image */}
                {[...Array(8)].map((_, i) => (
                  <motion.div
                    key={i}
                    className="absolute w-3 h-3 bg-gradient-to-r from-cyan-400 to-teal-400 rounded-full"
                    style={{
                      top: `${15 + i * 10}%`,
                      left: `${10 + (i % 4) * 20}%`,
                    }}
                    animate={{
                      y: [-15, 15, -15],
                      x: [-10, 10, -10],
                      opacity: [0.3, 1, 0.3],
                      scale: [0.5, 1, 0.5],
                    }}
                    transition={{
                      duration: 4 + i * 0.5,
                      repeat: Infinity,
                      ease: "easeInOut",
                      delay: i * 0.3,
                    }}
                  />
                ))}

                {/* Rotating Border */}
                <motion.div
                  className="absolute -inset-2 rounded-full border-2 border-gradient-to-r from-teal-400 to-cyan-400 opacity-50"
                  animate={{ rotate: 360 }}
                  transition={{
                    duration: 20,
                    repeat: Infinity,
                    ease: "linear"
                  }}
                  style={{
                    background: 'conic-gradient(from 0deg, transparent, rgba(20, 184, 166, 0.5), transparent)',
                    borderRadius: '50%',
                  }}
                />
              </motion.div>

              {/* Decorative Elements */}
              <motion.div
                className="absolute -top-10 -right-10 w-20 h-20 bg-gradient-to-r from-coral-400 to-orange-400 rounded-full opacity-30 blur-lg"
                animate={{
                  scale: [1, 1.2, 1],
                  opacity: [0.3, 0.6, 0.3],
                }}
                transition={{
                  duration: 4,
                  repeat: Infinity,
                  ease: "easeInOut"
                }}
              />
              
              <motion.div
                className="absolute -bottom-5 -left-5 w-16 h-16 border-2 border-cyan-400 rounded-lg opacity-40"
                animate={{
                  rotate: [0, 180, 360],
                }}
                transition={{
                  duration: 8,
                  repeat: Infinity,
                  ease: "linear"
                }}
              />
            </div>
          </motion.div>
        </div>
      </div>

      {/* Scroll Indicator */}
      <motion.div 
        className="absolute bottom-8 left-1/2 transform -translate-x-1/2"
        initial={{ opacity: 0, y: 20 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.8, delay: 2.5 }}
      >
        <motion.div
          className="flex flex-col items-center text-cyan-200"
          animate={{ y: [0, 10, 0] }}
          transition={{ duration: 2, repeat: Infinity, ease: "easeInOut" }}
        >
          <span className="text-sm mb-2 font-medium">Scroll to explore</span>
          <svg className="w-6 h-6" fill="none" stroke="currentColor" viewBox="0 0 24 24">
            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M19 14l-7 7m0 0l-7-7m7 7V3" />
          </svg>
        </motion.div>
      </motion.div>
    </section>
  );
};

export default Hero;
