import React, { useRef, useState, useEffect } from 'react';
import { motion, useScroll, useTransform, useInView, useAnimation } from 'framer-motion';
import './AboutAnimations.css';

const About = () => {
  const sectionRef = useRef(null);
  const isInView = useInView(sectionRef, { once: true, margin: "-100px" });
  const controls = useAnimation();
  
  const { scrollYProgress } = useScroll({
    target: sectionRef,
    offset: ["start end", "end start"]
  });

  const y = useTransform(scrollYProgress, [0, 1], [50, -50]);
  const opacity = useTransform(scrollYProgress, [0, 0.2, 0.8, 1], [0, 1, 1, 0]);

  // Animated counter hook
  const useCounter = (end, duration = 2) => {
    const [count, setCount] = useState(0);
    
    useEffect(() => {
      if (isInView) {
        let startTime;
        const animate = (timestamp) => {
          if (!startTime) startTime = timestamp;
          const progress = Math.min((timestamp - startTime) / (duration * 1000), 1);
          setCount(Math.floor(progress * end));
          if (progress < 1) requestAnimationFrame(animate);
        };
        requestAnimationFrame(animate);
      }
    }, [isInView, end, duration]);
    
    return count;
  };

  const projectCount = useCounter(50, 2);
  const experienceCount = useCounter(3, 2.5);
  const clientCount = useCounter(25, 3);

  const containerVariants = {
    hidden: { opacity: 0, y: 30 },
    visible: (i) => ({
      opacity: 1,
      y: 0,
      transition: {
        duration: 0.6,
        delay: i * 0.1,
        ease: "easeOut"
      }
    })
  };

  const highlights = [
    {
      title: "Who I Am",
      content: "A passionate MERN stack developer with a creative mindset and an eye for elegant solutions.",
      icon: "‚ú®",
      gradient: "from-teal-400 to-cyan-500"
    },
    {
      title: "What I Do",
      content: "I craft seamless digital experiences using MongoDB, Express.js, React.js, and Node.js.",
      icon: "üöÄ",
      gradient: "from-orange-400 to-red-500"
    },
    {
      title: "My Goal",
      content: "To become an independent, self-driven developer who creates meaningful impact through code.",
      icon: "üéØ",
      gradient: "from-purple-400 to-pink-500"
    }
  ];

  return (
    <section 
      ref={sectionRef}
      id="about" 
      className="relative min-h-screen bg-gradient-to-br from-slate-50 via-cyan-50 to-teal-50 overflow-hidden py-20"
    >
      {/* Flowing Background Elements */}
      <div className="absolute inset-0 overflow-hidden">
        {/* Animated Gradient Mesh */}
        <motion.div
          className="absolute inset-0 opacity-30"
          style={{ y }}
        >
          <div className="absolute top-20 left-10 w-96 h-96 bg-gradient-to-r from-teal-200 to-cyan-200 rounded-full blur-3xl opacity-40" />
          <div className="absolute bottom-20 right-10 w-80 h-80 bg-gradient-to-r from-orange-200 to-red-200 rounded-full blur-3xl opacity-40" />
          <div className="absolute top-1/2 left-1/2 transform -translate-x-1/2 -translate-y-1/2 w-64 h-64 bg-gradient-to-r from-purple-200 to-pink-200 rounded-full blur-3xl opacity-30" />
        </motion.div>

        {/* Floating Abstract Shapes */}
        <div className="absolute inset-0">
          {[...Array(8)].map((_, i) => (
            <motion.div
              key={i}
              className="absolute opacity-20"
              style={{
                left: `${15 + i * 12}%`,
                top: `${20 + (i % 3) * 25}%`,
                width: `${60 + (i % 3) * 30}px`,
                height: `${60 + (i % 3) * 30}px`,
              }}
              animate={{
                y: [-20, 20, -20],
                x: [-15, 15, -15],
                rotate: [0, 180, 360],
                scale: [0.8, 1.2, 0.8],
              }}
              transition={{
                duration: 12 + i * 2,
                repeat: Infinity,
                ease: "easeInOut",
                delay: i * 0.5,
              }}
            >
              <div 
                className="w-full h-full rounded-full"
                style={{
                  background: i % 3 === 0 
                    ? 'linear-gradient(135deg, rgba(20, 184, 166, 0.3), rgba(6, 182, 212, 0.1))'
                    : i % 3 === 1
                    ? 'linear-gradient(135deg, rgba(249, 115, 22, 0.3), rgba(234, 88, 12, 0.1))'
                    : 'linear-gradient(135deg, rgba(168, 85, 247, 0.3), rgba(236, 72, 153, 0.1))',
                  borderRadius: i % 2 === 0 ? '50%' : '30% 70% 70% 30% / 30% 30% 70% 70%'
                }}
              />
            </motion.div>
          ))}
        </div>

        {/* Flowing Lines */}
        <svg className="absolute inset-0 w-full h-full opacity-20" viewBox="0 0 1200 800" preserveAspectRatio="xMidYMid slice">
          <motion.path
            d="M0,400 Q300,200 600,400 T1200,400"
            stroke="url(#lineGradient1)"
            strokeWidth="2"
            fill="none"
            initial={{ pathLength: 0, opacity: 0 }}
            animate={{ pathLength: 1, opacity: 0.6 }}
            transition={{ duration: 2, ease: "easeInOut" }}
          />
          <motion.path
            d="M0,300 Q400,500 800,300 T1200,300"
            stroke="url(#lineGradient2)"
            strokeWidth="2"
            fill="none"
            initial={{ pathLength: 0, opacity: 0 }}
            animate={{ pathLength: 1, opacity: 0.4 }}
            transition={{ duration: 2.5, delay: 0.5, ease: "easeInOut" }}
          />
          <defs>
            <linearGradient id="lineGradient1" x1="0%" y1="0%" x2="100%" y2="0%">
              <stop offset="0%" stopColor="#14b8a6" />
              <stop offset="50%" stopColor="#06b6d4" />
              <stop offset="100%" stopColor="#3b82f6" />
            </linearGradient>
            <linearGradient id="lineGradient2" x1="0%" y1="0%" x2="100%" y2="0%">
              <stop offset="0%" stopColor="#f97316" />
              <stop offset="50%" stopColor="#ef4444" />
              <stop offset="100%" stopColor="#ec4899" />
            </linearGradient>
          </defs>
        </svg>
      </div>

      {/* Main Content */}
      <div className="relative z-10 max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
        <motion.div
          className="grid grid-cols-1 lg:grid-cols-12 gap-12 items-center min-h-screen"
          variants={containerVariants}
          initial="hidden"
          animate={isInView ? "visible" : "hidden"}
        >
          
          {/* Left Column - Main Content */}
          <div className="lg:col-span-7 space-y-12">
            
            {/* Section Title */}
            <motion.div variants={itemVariants} className="space-y-4">
              <motion.h2 
                className="text-5xl md:text-6xl lg:text-7xl font-black text-gray-900 leading-tight"
                initial={{ opacity: 0, x: -50 }}
                animate={isInView ? { opacity: 1, x: 0 } : {}}
                transition={{ duration: 0.8, delay: 0.2 }}
              >
                About{" "}
                <span className="relative inline-block">
                  <span className="bg-gradient-to-r from-teal-600 via-cyan-600 to-blue-600 bg-clip-text text-transparent">
                    Me
                  </span>
                  <motion.div
                    className="absolute bottom-0 left-0 h-2 bg-gradient-to-r from-teal-400 to-cyan-400 rounded-full"
                    initial={{ width: 0 }}
                    animate={isInView ? { width: "100%" } : {}}
                    transition={{ duration: 1, delay: 1 }}
                  />
                </span>
              </motion.h2>
              
              <motion.div
                className="w-24 h-1 bg-gradient-to-r from-teal-500 to-cyan-500 rounded-full"
                initial={{ scaleX: 0 }}
                animate={isInView ? { scaleX: 1 } : {}}
                transition={{ duration: 0.8, delay: 0.5 }}
              />
            </motion.div>

            {/* Story Content */}
            <motion.div variants={itemVariants} className="space-y-8">
              <div className="space-y-6">
                {[
                  "I'm a passionate developer who believes that great code is not just functional, but beautiful. My journey with the MERN stack began with curiosity and has evolved into a deep love for creating seamless digital experiences.",
                  "What drives me is the perfect blend of creativity and logic that programming offers. Every project is a new canvas where I can paint solutions that not only work flawlessly but also delight users with their elegance and intuition.",
                  "My goal is simple yet ambitious: to become an independent, self-driven developer who creates meaningful impact through code. I'm constantly learning, experimenting, and pushing the boundaries of what's possible in web development."
                ].map((text, index) => (
                  <motion.p
                    key={index}
                    className="text-lg md:text-xl text-gray-700 leading-relaxed"
                    variants={textRevealVariants}
                    custom={index}
                    initial="hidden"
                    animate={isInView ? "visible" : "hidden"}
                  >
                    {text}
                  </motion.p>
                ))}
              </div>
            </motion.div>

            {/* Skills Highlight */}
            <motion.div 
              variants={itemVariants}
              className="bg-white/60 backdrop-blur-sm rounded-2xl p-8 border border-white/20 shadow-xl"
            >
              <h3 className="text-2xl font-bold text-gray-900 mb-6">My Expertise</h3>
              <div className="grid grid-cols-2 md:grid-cols-4 gap-4">
                {['MongoDB', 'Express.js', 'React.js', 'Node.js'].map((skill, index) => (
                  <motion.div
                    key={skill}
                    className="text-center p-4 bg-gradient-to-br from-teal-50 to-cyan-50 rounded-xl border border-teal-100"
                    whileHover={{ scale: 1.05, y: -5 }}
                    initial={{ opacity: 0, y: 20 }}
                    animate={isInView ? { opacity: 1, y: 0 } : {}}
                    transition={{ duration: 0.5, delay: 0.8 + index * 0.1 }}
                  >
                    <div className="text-2xl mb-2">
                      {index === 0 ? 'üçÉ' : index === 1 ? '‚ö°' : index === 2 ? '‚öõÔ∏è' : 'üöÄ'}
                    </div>
                    <span className="font-semibold text-gray-800">{skill}</span>
                  </motion.div>
                ))}
              </div>
            </motion.div>
          </div>

          {/* Right Column - Highlight Cards */}
          <div className="lg:col-span-5 space-y-8">
            {highlights.map((highlight, index) => (
              <motion.div
                key={highlight.title}
                className="relative group"
                variants={itemVariants}
                initial={{ opacity: 0, x: 50, rotateY: 15 }}
                animate={isInView ? { opacity: 1, x: 0, rotateY: 0 } : {}}
                transition={{ duration: 0.8, delay: 0.5 + index * 0.2 }}
                whileHover={{ scale: 1.02, y: -5 }}
              >
                <div className="bg-white/70 backdrop-blur-md rounded-2xl p-8 border border-white/30 shadow-2xl overflow-hidden">
                  {/* Background Gradient */}
                  <div className={`absolute inset-0 bg-gradient-to-br ${highlight.gradient} opacity-5 group-hover:opacity-10 transition-opacity duration-300`} />
                  
                  {/* Content */}
                  <div className="relative z-10">
                    <div className="flex items-center space-x-4 mb-4">
                      <motion.div
                        className="text-3xl"
                        animate={{ rotate: [0, 10, -10, 0] }}
                        transition={{ duration: 2, repeat: Infinity, ease: "easeInOut" }}
                      >
                        {highlight.icon}
                      </motion.div>
                      <h3 className={`text-2xl font-bold bg-gradient-to-r ${highlight.gradient} bg-clip-text text-transparent`}>
                        {highlight.title}
                      </h3>
                    </div>
                    <p className="text-gray-700 leading-relaxed">
                      {highlight.content}
                    </p>
                  </div>

                  {/* Decorative Elements */}
                  <motion.div
                    className={`absolute -top-4 -right-4 w-24 h-24 bg-gradient-to-br ${highlight.gradient} rounded-full opacity-10 blur-xl`}
                    animate={{
                      scale: [1, 1.2, 1],
                      opacity: [0.1, 0.2, 0.1],
                    }}
                    transition={{
                      duration: 3,
                      repeat: Infinity,
                      ease: "easeInOut",
                      delay: index * 0.5
                    }}
                  />
                </div>
              </motion.div>
            ))}
          </div>
        </motion.div>

        {/* Bottom Statistics */}
        <motion.div
          className="mt-20 grid grid-cols-1 md:grid-cols-3 gap-8"
          initial={{ opacity: 0, y: 50 }}
          animate={isInView ? { opacity: 1, y: 0 } : {}}
          transition={{ duration: 0.8, delay: 1.5 }}
        >
          {[
            { number: "50+", label: "Projects Completed", color: "from-teal-400 to-cyan-500" },
            { number: "3+", label: "Years Learning", color: "from-orange-400 to-red-500" },
            { number: "‚àû", label: "Passion for Code", color: "from-purple-400 to-pink-500" }
          ].map((stat, index) => (
            <motion.div
              key={stat.label}
              className="text-center p-6 bg-white/50 backdrop-blur-sm rounded-2xl border border-white/20"
              whileHover={{ scale: 1.05, y: -5 }}
              initial={{ opacity: 0, y: 30 }}
              animate={isInView ? { opacity: 1, y: 0 } : {}}
              transition={{ duration: 0.6, delay: 1.7 + index * 0.1 }}
            >
              <motion.div
                className={`text-4xl md:text-5xl font-black bg-gradient-to-r ${stat.color} bg-clip-text text-transparent mb-2`}
                animate={{ scale: [1, 1.05, 1] }}
                transition={{ duration: 2, repeat: Infinity, ease: "easeInOut", delay: index * 0.3 }}
              >
                {stat.number}
              </motion.div>
              <div className="text-gray-600 font-medium">{stat.label}</div>
            </motion.div>
          ))}
        </motion.div>
      </div>
    </section>
  );
};

export default About;
