import React, { useState, useEffect } from 'react';
import { motion, AnimatePresence } from 'framer-motion';

const Navbar = () => {
  const [isOpen, setIsOpen] = useState(false);
  const [activeSection, setActiveSection] = useState('home');
  const [scrolled, setScrolled] = useState(false);

  const menuItems = [
    { name: 'Home', href: '#home', id: 'home' },
    { name: 'About', href: '#about', id: 'about' },
    { name: 'Projects', href: '#featured-projects', id: 'featured-projects' },
    { name: 'Contact', href: '#contact', id: 'contact' },
  ];

  useEffect(() => {
    const handleScroll = () => {
      setScrolled(window.scrollY > 50);
      
      // Update active section based on scroll position
      const sections = menuItems.map(item => document.getElementById(item.id)).filter(Boolean);
      const scrollPosition = window.scrollY + 100;
      
      for (let i = sections.length - 1; i >= 0; i--) {
        const section = sections[i];
        if (section && section.offsetTop <= scrollPosition) {
          setActiveSection(menuItems[i].id);
          break;
        }
      }
    };

    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, [menuItems]);

  const handleNavClick = (href, id) => {
    setActiveSection(id);
    setIsOpen(false);
    
    // Smooth scroll to section
    const element = document.querySelector(href);
    if (element) {
      element.scrollIntoView({ behavior: 'smooth' });
    }
  };

  return (
    <>
      {/* Animated Wave Background */}
      <div className="fixed top-0 left-0 right-0 z-40 h-20 overflow-hidden pointer-events-none">
        <svg
          className="absolute inset-0 w-full h-full"
          viewBox="0 0 1200 120"
          preserveAspectRatio="none"
        >
          <defs>
            <linearGradient id="waveGradient" x1="0%" y1="0%" x2="100%" y2="0%">
              <stop offset="0%" stopColor="rgba(37, 99, 235, 0.1)" />
              <stop offset="25%" stopColor="rgba(124, 58, 237, 0.15)" />
              <stop offset="50%" stopColor="rgba(13, 148, 136, 0.12)" />
              <stop offset="75%" stopColor="rgba(37, 99, 235, 0.1)" />
              <stop offset="100%" stopColor="rgba(124, 58, 237, 0.08)" />
            </linearGradient>
          </defs>
          
          {/* Multiple Wave Layers */}
          <motion.path
            d="M0,60 C300,20 600,100 900,60 C1050,40 1150,80 1200,60 L1200,0 L0,0 Z"
            fill="url(#waveGradient)"
            animate={{
              d: [
                "M0,60 C300,20 600,100 900,60 C1050,40 1150,80 1200,60 L1200,0 L0,0 Z",
                "M0,80 C300,40 600,80 900,40 C1050,60 1150,60 1200,80 L1200,0 L0,0 Z",
                "M0,60 C300,20 600,100 900,60 C1050,40 1150,80 1200,60 L1200,0 L0,0 Z"
              ]
            }}
            transition={{
              duration: 8,
              repeat: Infinity,
              ease: "easeInOut"
            }}
          />
          
          <motion.path
            d="M0,40 C300,80 600,20 900,40 C1050,60 1150,20 1200,40 L1200,0 L0,0 Z"
            fill="rgba(255, 255, 255, 0.8)"
            animate={{
              d: [
                "M0,40 C300,80 600,20 900,40 C1050,60 1150,20 1200,40 L1200,0 L0,0 Z",
                "M0,20 C300,60 600,40 900,20 C1050,40 1150,40 1200,20 L1200,0 L0,0 Z",
                "M0,40 C300,80 600,20 900,40 C1050,60 1150,20 1200,40 L1200,0 L0,0 Z"
              ]
            }}
            transition={{
              duration: 12,
              repeat: Infinity,
              ease: "easeInOut",
              delay: -2
            }}
          />
        </svg>
      </div>

      <motion.nav
        className={`fixed top-0 left-0 right-0 z-50 transition-all duration-500 ${
          scrolled 
            ? 'bg-white/20 backdrop-blur-xl shadow-lg border-b border-white/20' 
            : 'bg-white/10 backdrop-blur-md'
        }`}
        initial={{ y: -100, opacity: 0 }}
        animate={{ y: 0, opacity: 1 }}
        transition={{ duration: 0.8, ease: [0.25, 0.46, 0.45, 0.94] }}
      >
        {/* Floating Particles */}
        <div className="absolute inset-0 overflow-hidden pointer-events-none">
          {[...Array(6)].map((_, i) => (
            <motion.div
              key={i}
              className="absolute w-2 h-2 rounded-full opacity-30"
              style={{
                left: `${15 + i * 15}%`,
                top: '50%',
                background: i % 3 === 0 
                  ? 'linear-gradient(135deg, #2563eb, #3b82f6)'
                  : i % 3 === 1
                  ? 'linear-gradient(135deg, #7c3aed, #8b5cf6)'
                  : 'linear-gradient(135deg, #0d9488, #14b8a6)'
              }}
              animate={{
                y: [-10, 10, -10],
                x: [-5, 5, -5],
                scale: [0.8, 1.2, 0.8],
                opacity: [0.3, 0.7, 0.3]
              }}
              transition={{
                duration: 4 + i,
                repeat: Infinity,
                ease: "easeInOut",
                delay: i * 0.5
              }}
            />
          ))}
        </div>

        <div className="container-max relative z-10">
          <div className="flex items-center justify-between h-20">
            
            {/* Logo with Floating Animation */}
            <motion.div
              className="flex items-center space-x-3 cursor-pointer group"
              initial={{ scale: 1 }}
              animate={{ 
                y: [-2, 2, -2],
                scale: [1, 1.02, 1] 
              }}
              transition={{ 
                duration: 4, 
                repeat: Infinity, 
                ease: "easeInOut" 
              }}
              onClick={() => handleNavClick('#home', 'home')}
              whileHover={{ scale: 1.05 }}
              whileTap={{ scale: 0.95 }}
            >
              {/* Morphing Logo Container */}
              <motion.div
                className="relative w-12 h-12 bg-gradient-to-br from-blue-500 via-purple-500 to-teal-500 flex items-center justify-center shadow-lg overflow-hidden"
                animate={{
                  borderRadius: [
                    "30% 70% 70% 30% / 30% 30% 70% 70%",
                    "70% 30% 30% 70% / 70% 70% 30% 30%",
                    "30% 70% 70% 30% / 30% 30% 70% 70%"
                  ]
                }}
                transition={{
                  duration: 6,
                  repeat: Infinity,
                  ease: "easeInOut"
                }}
              >
                {/* Rotating Inner Glow */}
                <motion.div
                  className="absolute inset-1 bg-white/20 backdrop-blur-sm"
                  animate={{
                    borderRadius: [
                      "70% 30% 30% 70% / 70% 70% 30% 30%",
                      "30% 70% 70% 30% / 30% 30% 70% 70%",
                      "70% 30% 30% 70% / 70% 70% 30% 30%"
                    ],
                    rotate: [0, 180, 360]
                  }}
                  transition={{
                    duration: 8,
                    repeat: Infinity,
                    ease: "linear"
                  }}
                />
                
                <span className="relative z-10 text-white font-bold text-xl">R</span>
              </motion.div>
              
              <div className="hidden sm:block">
                <motion.h1 
                  className="text-xl font-bold text-gradient"
                  animate={{
                    backgroundPosition: ['0% 50%', '100% 50%', '0% 50%']
                  }}
                  transition={{
                    duration: 5,
                    repeat: Infinity,
                    ease: "easeInOut"
                  }}
                >
                  Rabeya Khatun
                </motion.h1>
                <p className="text-xs text-secondary">MERN Stack Developer</p>
              </div>
            </motion.div>

            {/* Desktop Menu with Wave Interactions */}
            <div className="hidden lg:flex items-center space-x-2">
              {menuItems.map((item, index) => (
                <motion.div
                  key={item.id}
                  className="relative"
                  initial={{ opacity: 0, y: -20 }}
                  animate={{ opacity: 1, y: 0 }}
                  transition={{ delay: index * 0.1 + 0.3 }}
                >
                  <motion.button
                    className={`relative px-6 py-3 rounded-2xl font-medium transition-all duration-300 overflow-hidden group ${
                      activeSection === item.id
                        ? 'text-white'
                        : 'text-primary hover:text-white'
                    }`}
                    onClick={() => handleNavClick(item.href, item.id)}
                    whileHover={{ 
                      scale: 1.05,
                      y: -2
                    }}
                    whileTap={{ scale: 0.95 }}
                  >
                    {/* Ripple Effect Background */}
                    <motion.div
                      className={`absolute inset-0 bg-gradient-to-r from-blue-500 via-purple-500 to-teal-500 rounded-2xl transition-all duration-300 ${
                        activeSection === item.id ? 'opacity-100' : 'opacity-0 group-hover:opacity-80'
                      }`}
                      whileHover={{
                        scale: [1, 1.2, 1],
                        rotate: [0, 5, 0]
                      }}
                      transition={{ duration: 0.6 }}
                    />
                    
                    {/* Floating Wave Underline */}
                    {activeSection === item.id && (
                      <motion.div
                        className="absolute bottom-0 left-0 right-0 h-1 bg-gradient-to-r from-blue-400 to-teal-400 rounded-full"
                        layoutId="activeWave"
                        animate={{
                          scaleX: [1, 1.1, 1],
                          opacity: [0.8, 1, 0.8]
                        }}
                        transition={{
                          duration: 2,
                          repeat: Infinity,
                          ease: "easeInOut"
                        }}
                      />
                    )}
                    
                    <span className="relative z-10">{item.name}</span>
                  </motion.button>
                </motion.div>
              ))}
            </div>

            {/* CTA Button with Wave Border */}
            <div className="hidden lg:block">
              <motion.button
                className="group relative px-6 py-3 bg-gradient-to-r from-blue-500 via-purple-500 to-teal-500 text-white font-semibold rounded-2xl overflow-hidden shadow-lg"
                onClick={() => handleNavClick('#contact', 'contact')}
                whileHover={{ 
                  scale: 1.05,
                  boxShadow: "0 20px 40px rgba(37, 99, 235, 0.3)"
                }}
                whileTap={{ scale: 0.95 }}
                initial={{ opacity: 0, x: 20 }}
                animate={{ opacity: 1, x: 0 }}
                transition={{ delay: 0.8 }}
              >
                {/* Animated Border Wave */}
                <motion.div
                  className="absolute inset-0 bg-gradient-to-r from-teal-400 via-blue-400 to-purple-400 rounded-2xl"
                  animate={{
                    rotate: [0, 360],
                    scale: [1, 1.05, 1]
                  }}
                  transition={{
                    duration: 4,
                    repeat: Infinity,
                    ease: "linear"
                  }}
                />
                
                <motion.div
                  className="relative bg-gradient-to-r from-blue-500 via-purple-500 to-teal-500 rounded-2xl px-6 py-3 m-0.5"
                  whileHover={{
                    background: "linear-gradient(45deg, #3b82f6, #8b5cf6, #14b8a6)"
                  }}
                >
                  <span className="relative z-10 flex items-center">
                    Hire Me
                    <motion.svg 
                      className="w-4 h-4 ml-2" 
                      fill="none" 
                      stroke="currentColor" 
                      viewBox="0 0 24 24"
                      animate={{ x: [0, 3, 0] }}
                      transition={{ duration: 1.5, repeat: Infinity }}
                    >
                      <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M17 8l4 4m0 0l-4 4m4-4H3" />
                    </motion.svg>
                  </span>
                </motion.div>
              </motion.button>
            </div>

            {/* Mobile Menu Button with Morphing Animation */}
            <motion.button
              className="lg:hidden relative p-3 rounded-xl bg-white/10 backdrop-blur-sm border border-white/20 hover:bg-white/20 transition-all duration-300"
              onClick={() => setIsOpen(!isOpen)}
              whileTap={{ scale: 0.95 }}
              whileHover={{ scale: 1.05 }}
            >
              <div className="w-6 h-6 flex flex-col justify-center items-center">
                <motion.span
                  className="block h-0.5 w-6 bg-primary rounded-full"
                  animate={{
                    rotate: isOpen ? 45 : 0,
                    y: isOpen ? 6 : 0,
                  }}
                  transition={{ duration: 0.3 }}
                />
                <motion.span
                  className="block h-0.5 w-6 bg-primary rounded-full mt-1.5"
                  animate={{
                    opacity: isOpen ? 0 : 1,
                    scale: isOpen ? 0 : 1
                  }}
                  transition={{ duration: 0.3 }}
                />
                <motion.span
                  className="block h-0.5 w-6 bg-primary rounded-full mt-1.5"
                  animate={{
                    rotate: isOpen ? -45 : 0,
                    y: isOpen ? -6 : 0,
                  }}
                  transition={{ duration: 0.3 }}
                />
              </div>
            </motion.button>
          </div>
        </div>

        {/* Mobile Menu with Wave Animation */}
        <AnimatePresence>
          {isOpen && (
            <motion.div
              className="lg:hidden absolute top-full left-0 right-0 bg-white/95 backdrop-blur-xl border-t border-white/20 shadow-xl"
              initial={{ opacity: 0, height: 0, y: -20 }}
              animate={{ opacity: 1, height: 'auto', y: 0 }}
              exit={{ opacity: 0, height: 0, y: -20 }}
              transition={{ duration: 0.4, ease: [0.25, 0.46, 0.45, 0.94] }}
            >
              {/* Mobile Menu Wave Background */}
              <div className="absolute inset-0 overflow-hidden">
                <motion.div
                  className="absolute -top-10 left-0 right-0 h-20 bg-gradient-to-r from-blue-100/50 via-purple-100/50 to-teal-100/50 rounded-b-[50%]"
                  animate={{
                    scaleY: [1, 1.1, 1],
                    opacity: [0.5, 0.8, 0.5]
                  }}
                  transition={{
                    duration: 3,
                    repeat: Infinity,
                    ease: "easeInOut"
                  }}
                />
              </div>

              <div className="container-max py-6 relative z-10">
                <div className="flex flex-col space-y-2">
                  {menuItems.map((item, index) => (
                    <motion.button
                      key={item.id}
                      className={`text-left px-6 py-4 rounded-xl font-medium transition-all duration-300 ${
                        activeSection === item.id
                          ? 'text-white bg-gradient-to-r from-blue-500 to-purple-500'
                          : 'text-primary hover:text-white hover:bg-gradient-to-r hover:from-blue-400 hover:to-teal-400'
                      }`}
                      onClick={() => handleNavClick(item.href, item.id)}
                      initial={{ opacity: 0, x: -30 }}
                      animate={{ opacity: 1, x: 0 }}
                      transition={{ delay: index * 0.1 }}
                      whileTap={{ scale: 0.98 }}
                    >
                      {item.name}
                    </motion.button>
                  ))}
                  
                  <motion.button
                    className="btn btn-primary mt-4"
                    onClick={() => handleNavClick('#contact', 'contact')}
                    initial={{ opacity: 0, y: 20 }}
                    animate={{ opacity: 1, y: 0 }}
                    transition={{ delay: 0.5 }}
                  >
                    Hire Me
                  </motion.button>
                </div>
              </div>
            </motion.div>
          )}
        </AnimatePresence>
      </motion.nav>
    </>
  );
};

export default Navbar;
