import React, { useState, useEffect, useRef } from 'react';
import { motion, useScroll, useTransform, useSpring } from 'framer-motion';

const Hero = () => {
  const [mousePosition, setMousePosition] = useState({ x: 0, y: 0 });
  const heroRef = useRef(null);
  
  const { scrollYProgress } = useScroll({
    target: heroRef,
    offset: ["start start", "end start"]
  });

  const y = useTransform(scrollYProgress, [0, 1], [0, -150]);
  const opacity = useTransform(scrollYProgress, [0, 0.8], [1, 0]);
  
  // Smooth mouse following
  const mouseX = useSpring(0, { stiffness: 500, damping: 100 });
  const mouseY = useSpring(0, { stiffness: 500, damping: 100 });

  useEffect(() => {
    const handleMouseMove = (e) => {
      const { clientX, clientY } = e;
      const { innerWidth, innerHeight } = window;
      
      setMousePosition({
        x: (clientX / innerWidth) * 100,
        y: (clientY / innerHeight) * 100,
      });
      
      mouseX.set((clientX / innerWidth - 0.5) * 30);
      mouseY.set((clientY / innerHeight - 0.5) * 30);
    };

    window.addEventListener('mousemove', handleMouseMove);
    return () => window.removeEventListener('mousemove', handleMouseMove);
  }, [mouseX, mouseY]);

  // Letter-by-letter animation variants
  const titleVariants = {
    hidden: { opacity: 0 },
    visible: {
      opacity: 1,
      transition: {
        staggerChildren: 0.08,
        delayChildren: 0.3
      }
    }
  };

  const letterVariants = {
    hidden: { 
      opacity: 0, 
      y: 120,
      rotateX: -90,
      scale: 0.3
    },
    visible: {
      opacity: 1,
      y: 0,
      rotateX: 0,
      scale: 1,
      transition: {
        duration: 1,
        ease: [0.25, 0.46, 0.45, 0.94],
        type: "spring",
        stiffness: 80
      }
    }
  };

  // Wave floating animation for letters
  const createFloatingVariants = (delay = 0) => ({
    animate: {
      y: [-12, 12, -12],
      rotate: [-2, 2, -2],
      transition: {
        duration: 4 + delay,
        repeat: Infinity,
        ease: "easeInOut",
        delay: delay
      }
    }
  });

  const containerVariants = {
    hidden: { opacity: 0 },
    visible: {
      opacity: 1,
      transition: {
        staggerChildren: 0.2,
        delayChildren: 1.2
      }
    }
  };

  const itemVariants = {
    hidden: { opacity: 0, y: 60, scale: 0.8 },
    visible: {
      opacity: 1,
      y: 0,
      scale: 1,
      transition: {
        duration: 1,
        ease: [0.25, 0.46, 0.45, 0.94]
      }
    }
  };

  // Morphing blob animation
  const blobVariants = {
    animate: {
      borderRadius: [
        "60% 40% 30% 70% / 60% 30% 70% 40%",
        "30% 60% 70% 40% / 50% 60% 30% 60%",
        "50% 40% 60% 30% / 70% 50% 40% 60%",
        "40% 70% 50% 60% / 40% 60% 50% 70%",
        "60% 40% 30% 70% / 60% 30% 70% 40%"
      ],
      transition: {
        duration: 12,
        repeat: Infinity,
        ease: "easeInOut"
      }
    }
  };

  return (
    <section 
      ref={heroRef}
      id="home" 
      className="relative min-h-screen flex items-center justify-center overflow-hidden pt-28"
    >
      {/* Dynamic Gradient Background matching navbar colors */}
      <div 
        className="absolute inset-0 opacity-50 transition-all duration-1000"
        style={{
          background: `
            radial-gradient(circle at ${mousePosition.x}% ${mousePosition.y}%, 
              rgba(20, 184, 166, 0.2) 0%, 
              rgba(255, 127, 80, 0.15) 25%, 
              rgba(34, 197, 94, 0.18) 50%, 
              rgba(251, 191, 36, 0.12) 75%, 
              transparent 100%
            )
          `
        }}
      />

      {/* Animated Mesh Background */}
      <div className="absolute inset-0 opacity-40">
        <motion.div 
          className="absolute top-20 left-20 w-96 h-96 rounded-full blur-3xl"
          style={{
            background: 'linear-gradient(135deg, rgba(20, 184, 166, 0.3), rgba(34, 197, 94, 0.2))'
          }}
          animate={{
            x: [0, 120, 0],
            y: [0, -80, 0],
            scale: [1, 1.3, 1],
          }}
          transition={{
            duration: 25,
            repeat: Infinity,
            ease: "easeInOut"
          }}
        />
        <motion.div 
          className="absolute bottom-20 right-20 w-80 h-80 rounded-full blur-3xl"
          style={{
            background: 'linear-gradient(135deg, rgba(255, 127, 80, 0.3), rgba(251, 191, 36, 0.2))'
          }}
          animate={{
            x: [0, -100, 0],
            y: [0, 80, 0],
            scale: [1, 0.7, 1],
          }}
          transition={{
            duration: 30,
            repeat: Infinity,
            ease: "easeInOut",
            delay: -8
          }}
        />
        <motion.div 
          className="absolute top-1/2 left-1/2 w-72 h-72 rounded-full blur-3xl"
          style={{
            background: 'linear-gradient(135deg, rgba(34, 197, 94, 0.25), rgba(6, 78, 59, 0.2))'
          }}
          animate={{
            x: [-50, 50, -50],
            y: [-60, 60, -60],
            scale: [0.8, 1.2, 0.8],
          }}
          transition={{
            duration: 20,
            repeat: Infinity,
            ease: "easeInOut",
            delay: -15
          }}
        />
      </div>

      {/* Floating Particles System */}
      <div className="absolute inset-0 overflow-hidden pointer-events-none">
        {[...Array(15)].map((_, i) => (
          <motion.div
            key={i}
            className="absolute opacity-30"
            style={{
              left: `${8 + i * 6}%`,
              top: `${10 + (i % 5) * 18}%`,
              width: `${15 + (i % 4) * 12}px`,
              height: `${15 + (i % 4) * 12}px`,
            }}
            animate={{
              y: [-40, 40, -40],
              x: [-25, 25, -25],
              rotate: [0, 360],
              scale: [0.4, 1.8, 0.4],
            }}
            transition={{
              duration: 18 + i * 2,
              repeat: Infinity,
              ease: "easeInOut",
              delay: i * 1.2,
            }}
          >
            <div 
              className="w-full h-full"
              style={{
                background: i % 5 === 0 
                  ? 'linear-gradient(135deg, rgba(20, 184, 166, 0.6), rgba(34, 197, 94, 0.3))'
                  : i % 5 === 1
                  ? 'linear-gradient(135deg, rgba(255, 127, 80, 0.6), rgba(251, 191, 36, 0.3))'
                  : i % 5 === 2
                  ? 'linear-gradient(135deg, rgba(34, 197, 94, 0.6), rgba(6, 78, 59, 0.3))'
                  : i % 5 === 3
                  ? 'linear-gradient(135deg, rgba(251, 191, 36, 0.6), rgba(255, 127, 80, 0.3))'
                  : 'linear-gradient(135deg, rgba(6, 78, 59, 0.6), rgba(20, 184, 166, 0.3))',
                borderRadius: i % 6 === 0 ? '50%' 
                  : i % 6 === 1 ? '30% 70% 70% 30% / 30% 30% 70% 70%' 
                  : i % 6 === 2 ? '25% 75% 25% 75% / 75% 25% 75% 25%'
                  : i % 6 === 3 ? '40% 60% 60% 40% / 60% 30% 70% 40%'
                  : i % 6 === 4 ? '70% 30% 50% 50% / 30% 70% 50% 50%'
                  : '50% 50% 30% 70% / 50% 50% 70% 30%'
              }}
            />
          </motion.div>
        ))}
      </div>

      {/* Main Content Container */}
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 relative z-10">
        <div className="grid grid-cols-1 lg:grid-cols-2 gap-12 lg:gap-20 items-center min-h-screen">
          
          {/* Left Column - Content */}
          <motion.div 
            className="space-y-10 text-center lg:text-left order-2 lg:order-1"
            style={{ y, opacity }}
          >
            
            {/* Greeting Animation */}
            <motion.div
              initial={{ opacity: 0, x: -60 }}
              animate={{ opacity: 1, x: 0 }}
              transition={{ duration: 1, delay: 0.2 }}
            >
              <motion.p 
                className="text-xl font-semibold tracking-wide"
                animate={{ 
                  background: [
                    'linear-gradient(45deg, #14b8a6, #22c55e)',
                    'linear-gradient(45deg, #ff7f50, #fbbf24)',
                    'linear-gradient(45deg, #22c55e, #064e3b)',
                    'linear-gradient(45deg, #14b8a6, #22c55e)'
                  ]
                }}
                transition={{
                  duration: 6,
                  repeat: Infinity,
                  ease: "easeInOut"
                }}
                style={{
                  backgroundClip: 'text',
                  WebkitBackgroundClip: 'text',
                  color: 'transparent'
                }}
              >
                Hi, I am Rabeya Khatun, a MERN Stack Developer
              </motion.p>
            </motion.div>

            {/* Main Title with Advanced Letter Animation */}
            <motion.div
              className="relative"
              variants={titleVariants}
              initial="hidden"
              animate="visible"
            >
              <div className="flex flex-wrap justify-center lg:justify-start leading-none">
                {"Crafting Digital".split('').map((letter, index) => (
                  <motion.span
                    key={`word1-${index}`}
                    className="text-5xl md:text-6xl lg:text-7xl font-black inline-block"
                    variants={letterVariants}
                    animate={{
                      ...createFloatingVariants(index * 0.1).animate,
                      background: [
                        'linear-gradient(135deg, #14b8a6, #22c55e)',
                        'linear-gradient(135deg, #ff7f50, #fbbf24)',
                        'linear-gradient(135deg, #22c55e, #064e3b)',
                        'linear-gradient(135deg, #14b8a6, #22c55e)'
                      ]
                    }}
                    transition={{
                      ...createFloatingVariants(index * 0.1).animate.transition,
                      background: {
                        duration: 8,
                        repeat: Infinity,
                        ease: "easeInOut",
                        delay: index * 0.2
                      }
                    }}
                    style={{
                      backgroundClip: 'text',
                      WebkitBackgroundClip: 'text',
                      color: 'transparent'
                    }}
                    whileHover={{
                      scale: 1.3,
                      rotate: [0, -15, 15, 0],
                      transition: { duration: 0.6 }
                    }}
                  >
                    {letter === ' ' ? '\u00A0' : letter}
                  </motion.span>
                ))}
              </div>
              <div className="flex flex-wrap justify-center lg:justify-start leading-none mt-4">
                {"Experiences".split('').map((letter, index) => (
                  <motion.span
                    key={`word2-${index}`}
                    className="text-5xl md:text-6xl lg:text-7xl font-black inline-block"
                    variants={letterVariants}
                    animate={{
                      ...createFloatingVariants((index + 15) * 0.1).animate,
                      background: [
                        'linear-gradient(135deg, #ff7f50, #fbbf24)',
                        'linear-gradient(135deg, #22c55e, #064e3b)',
                        'linear-gradient(135deg, #14b8a6, #22c55e)',
                        'linear-gradient(135deg, #ff7f50, #fbbf24)'
                      ]
                    }}
                    transition={{
                      ...createFloatingVariants((index + 15) * 0.1).animate.transition,
                      background: {
                        duration: 8,
                        repeat: Infinity,
                        ease: "easeInOut",
                        delay: (index + 15) * 0.2
                      }
                    }}
                    style={{
                      backgroundClip: 'text',
                      WebkitBackgroundClip: 'text',
                      color: 'transparent'
                    }}
                    whileHover={{
                      scale: 1.3,
                      rotate: [0, 15, -15, 0],
                      transition: { duration: 0.6 }
                    }}
                  >
                    {letter === ' ' ? '\u00A0' : letter}
                  </motion.span>
                ))}
              </div>
              
              {/* Animated Wave Underline */}
              <motion.div
                className="w-40 h-2 rounded-full mx-auto lg:mx-0 mt-8"
                initial={{ scaleX: 0 }}
                animate={{ 
                  scaleX: 1,
                  background: [
                    'linear-gradient(90deg, #14b8a6, #22c55e)',
                    'linear-gradient(90deg, #ff7f50, #fbbf24)',
                    'linear-gradient(90deg, #22c55e, #064e3b)',
                    'linear-gradient(90deg, #14b8a6, #22c55e)'
                  ]
                }}
                transition={{ 
                  scaleX: { duration: 2, delay: 2.5 },
                  background: { duration: 6, repeat: Infinity, ease: "easeInOut" }
                }}
              />
            </motion.div>

            {/* Subtitle */}
            <motion.div
              variants={containerVariants}
              initial="hidden"
              animate="visible"
            >
              <motion.p 
                className="text-xl text-gray-600 max-w-2xl mx-auto lg:mx-0 leading-relaxed"
                variants={itemVariants}
              >
                Passionate about creating modern web applications with clean code, 
                innovative solutions, and exceptional user experiences that make a lasting impact.
              </motion.p>
            </motion.div>

            {/* CTA Button with Wave Effects */}
            <motion.div
              variants={containerVariants}
              initial="hidden"
              animate="visible"
              className="flex justify-center lg:justify-start"
            >
              <motion.button
                className="group relative px-10 py-5 font-bold text-lg rounded-2xl overflow-hidden shadow-2xl"
                variants={itemVariants}
                whileHover={{ scale: 1.08, y: -3 }}
                whileTap={{ scale: 0.95 }}
                onClick={() => {
                  const element = document.querySelector('#projects');
                  if (element) element.scrollIntoView({ behavior: 'smooth' });
                }}
              >
                {/* Animated Background */}
                <motion.div
                  className="absolute inset-0"
                  animate={{
                    background: [
                      'linear-gradient(135deg, #14b8a6, #22c55e)',
                      'linear-gradient(135deg, #ff7f50, #fbbf24)',
                      'linear-gradient(135deg, #22c55e, #064e3b)',
                      'linear-gradient(135deg, #14b8a6, #22c55e)'
                    ]
                  }}
                  transition={{
                    duration: 4,
                    repeat: Infinity,
                    ease: "easeInOut"
                  }}
                />
                
                {/* Wave Border Effect */}
                <motion.div
                  className="absolute inset-0 rounded-2xl"
                  style={{
                    background: 'linear-gradient(45deg, transparent 30%, rgba(255,255,255,0.4) 50%, transparent 70%)'
                  }}
                  animate={{
                    rotate: [0, 360]
                  }}
                  transition={{
                    duration: 3,
                    repeat: Infinity,
                    ease: "linear"
                  }}
                />
                
                {/* Ripple Effect */}
                <motion.div
                  className="absolute inset-0 bg-white/20 rounded-2xl scale-0 group-hover:scale-150 opacity-0 group-hover:opacity-100"
                  transition={{ duration: 0.8 }}
                />
                
                <span className="relative z-10 flex items-center text-white">
                  View My Work
                  <motion.svg 
                    className="w-6 h-6 ml-3" 
                    fill="none" 
                    stroke="currentColor" 
                    viewBox="0 0 24 24"
                    animate={{ x: [0, 6, 0] }}
                    transition={{ duration: 2, repeat: Infinity }}
                  >
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M17 8l4 4m0 0l-4 4m4-4H3" />
                  </motion.svg>
                </span>
              </motion.button>
            </motion.div>

            {/* Scroll Indicator */}
            <motion.div
              className="hidden lg:flex items-center space-x-4 pt-12"
              initial={{ opacity: 0, y: 30 }}
              animate={{ opacity: 1, y: 0 }}
              transition={{ delay: 4, duration: 0.8 }}
            >
              <motion.div
                className="w-10 h-16 border-2 border-gray-400 rounded-full flex justify-center relative"
                animate={{ y: [0, 8, 0] }}
                transition={{ duration: 2.5, repeat: Infinity, ease: "easeInOut" }}
              >
                <motion.div
                  className="w-2 h-4 rounded-full mt-3"
                  animate={{ 
                    y: [0, 20, 0], 
                    opacity: [1, 0.3, 1],
                    background: [
                      'linear-gradient(180deg, #14b8a6, #22c55e)',
                      'linear-gradient(180deg, #ff7f50, #fbbf24)',
                      'linear-gradient(180deg, #22c55e, #064e3b)',
                      'linear-gradient(180deg, #14b8a6, #22c55e)'
                    ]
                  }}
                  transition={{ 
                    y: { duration: 2.5, repeat: Infinity, ease: "easeInOut" },
                    opacity: { duration: 2.5, repeat: Infinity, ease: "easeInOut" },
                    background: { duration: 6, repeat: Infinity, ease: "easeInOut" }
                  }}
                />
              </motion.div>
              <span className="text-sm text-gray-500 font-medium">Scroll to explore</span>
            </motion.div>
          </motion.div>

          {/* Right Column - Profile Image */}
          <motion.div
            className="flex justify-center order-1 lg:order-2"
            initial={{ opacity: 0, x: 120, scale: 0.7 }}
            animate={{ opacity: 1, x: 0, scale: 1 }}
            transition={{ delay: 2, duration: 1.5, ease: [0.25, 0.46, 0.45, 0.94] }}
          >
            <div className="relative">
              
              {/* Main Profile Container with Morphing Shape */}
              <motion.div
                className="relative w-80 h-80 md:w-96 md:h-96 lg:w-[450px] lg:h-[450px]"
                style={{ x: mouseX, y: mouseY }}
                animate={{ 
                  rotate: [0, 3, -3, 0],
                  scale: [1, 1.03, 1]
                }}
                transition={{ 
                  duration: 10, 
                  repeat: Infinity, 
                  ease: "easeInOut" 
                }}
              >
                {/* Morphing Blob Background */}
                <motion.div
                  className="absolute inset-6 backdrop-blur-2xl border border-white/40 shadow-2xl overflow-hidden"
                  variants={blobVariants}
                  animate="animate"
                  style={{
                    background: 'linear-gradient(135deg, rgba(255,255,255,0.9), rgba(255,255,255,0.7))'
                  }}
                >
                  {/* Profile Image */}
                  <img 
                    src="https://via.placeholder.com/450x450/14b8a6/ffffff?text=RK" 
                    alt="Rabeya Khatun" 
                    className="w-full h-full object-cover"
                  />
                  
                  {/* Overlay Gradient */}
                  <motion.div 
                    className="absolute inset-0"
                    animate={{
                      background: [
                        'linear-gradient(45deg, rgba(20, 184, 166, 0.1), rgba(34, 197, 94, 0.05))',
                        'linear-gradient(45deg, rgba(255, 127, 80, 0.1), rgba(251, 191, 36, 0.05))',
                        'linear-gradient(45deg, rgba(34, 197, 94, 0.1), rgba(6, 78, 59, 0.05))',
                        'linear-gradient(45deg, rgba(20, 184, 166, 0.1), rgba(34, 197, 94, 0.05))'
                      ]
                    }}
                    transition={{
                      duration: 8,
                      repeat: Infinity,
                      ease: "easeInOut"
                    }}
                  />
                </motion.div>

                {/* Floating Glow Effect */}
                <motion.div
                  className="absolute -inset-3 rounded-full blur-2xl opacity-70"
                  animate={{
                    scale: [1, 1.15, 1],
                    opacity: [0.7, 0.9, 0.7],
                    background: [
                      'linear-gradient(135deg, rgba(20, 184, 166, 0.4), rgba(34, 197, 94, 0.3))',
                      'linear-gradient(135deg, rgba(255, 127, 80, 0.4), rgba(251, 191, 36, 0.3))',
                      'linear-gradient(135deg, rgba(34, 197, 94, 0.4), rgba(6, 78, 59, 0.3))',
                      'linear-gradient(135deg, rgba(20, 184, 166, 0.4), rgba(34, 197, 94, 0.3))'
                    ]
                  }}
                  transition={{
                    duration: 6,
                    repeat: Infinity,
                    ease: "easeInOut"
                  }}
                />
              </motion.div>

              {/* Orbiting Tech Elements */}
              {[
                { tech: 'React', colors: ['#14b8a6', '#22c55e'], angle: 0 },
                { tech: 'Node', colors: ['#22c55e', '#064e3b'], angle: 90 },
                { tech: 'MongoDB', colors: ['#ff7f50', '#fbbf24'], angle: 180 },
                { tech: 'Express', colors: ['#fbbf24', '#14b8a6'], angle: 270 }
              ].map((item, index) => (
                <motion.div
                  key={item.tech}
                  className="absolute w-20 h-20 rounded-2xl shadow-2xl flex items-center justify-center text-white font-bold text-sm backdrop-blur-sm border border-white/30"
                  style={{
                    top: '50%',
                    left: '50%',
                    transformOrigin: '0 0',
                    background: `linear-gradient(135deg, ${item.colors[0]}, ${item.colors[1]})`
                  }}
                  animate={{
                    rotate: [item.angle, item.angle + 360],
                    x: [
                      Math.cos((item.angle * Math.PI) / 180) * 200 - 40,
                      Math.cos(((item.angle + 360) * Math.PI) / 180) * 200 - 40
                    ],
                    y: [
                      Math.sin((item.angle * Math.PI) / 180) * 200 - 40,
                      Math.sin(((item.angle + 360) * Math.PI) / 180) * 200 - 40
                    ],
                    scale: [1, 1.15, 1]
                  }}
                  transition={{
                    duration: 25,
                    repeat: Infinity,
                    ease: "linear",
                    delay: index * 0.8
                  }}
                  whileHover={{ 
                    scale: 1.4, 
                    rotate: [0, 20, -20, 0],
                    transition: { duration: 0.4 }
                  }}
                >
                  {item.tech}
                </motion.div>
              ))}

              {/* Decorative Floating Elements */}
              {[...Array(12)].map((_, i) => (
                <motion.div
                  key={`deco-${i}`}
                  className="absolute w-3 h-3 rounded-full"
                  style={{
                    top: `${15 + (i % 4) * 22}%`,
                    left: `${10 + (i % 3) * 30}%`,
                    background: i % 4 === 0 
                      ? 'linear-gradient(135deg, #14b8a6, #22c55e)'
                      : i % 4 === 1
                      ? 'linear-gradient(135deg, #ff7f50, #fbbf24)'
                      : i % 4 === 2
                      ? 'linear-gradient(135deg, #22c55e, #064e3b)'
                      : 'linear-gradient(135deg, #fbbf24, #14b8a6)'
                  }}
                  animate={{
                    y: [-30, 30, -30],
                    opacity: [0.4, 1, 0.4],
                    scale: [0.5, 2, 0.5]
                  }}
                  transition={{
                    duration: 6 + i,
                    repeat: Infinity,
                    ease: "easeInOut",
                    delay: i * 0.8
                  }}
                />
              ))}
            </div>
          </motion.div>
        </div>
      </div>
    </section>
  );
};

export default Hero;
