import React, { useState, useEffect, useRef } from 'react';
import { motion, useScroll, useTransform } from 'framer-motion';

const Hero = () => {
  const [mousePosition, setMousePosition] = useState({ x: 0, y: 0 });
  const heroRef = useRef(null);
  
  const { scrollYProgress } = useScroll({
    target: heroRef,
    offset: ["start start", "end start"]
  });

  const y = useTransform(scrollYProgress, [0, 1], [0, -100]);
  const opacity = useTransform(scrollYProgress, [0, 0.8], [1, 0]);

  useEffect(() => {
    const handleMouseMove = (e) => {
      setMousePosition({
        x: (e.clientX / window.innerWidth) * 100,
        y: (e.clientY / window.innerHeight) * 100,
      });
    };

    window.addEventListener('mousemove', handleMouseMove);
    return () => window.removeEventListener('mousemove', handleMouseMove);
  }, []);

  const titleVariants = {
    hidden: { opacity: 0 },
    visible: {
      opacity: 1,
      transition: {
        staggerChildren: 0.08,
        delayChildren: 0.3
      }
    }
  };

  const letterVariants = {
    hidden: { 
      opacity: 0, 
      y: 50,
      rotateX: -90
    },
    visible: {
      opacity: 1,
      y: 0,
      rotateX: 0,
      transition: {
        duration: 0.8,
        ease: [0.25, 0.46, 0.45, 0.94]
      }
    }
  };

  const containerVariants = {
    hidden: { opacity: 0 },
    visible: {
      opacity: 1,
      transition: {
        staggerChildren: 0.2,
        delayChildren: 0.5
      }
    }
  };

  const itemVariants = {
    hidden: { opacity: 0, y: 30 },
    visible: {
      opacity: 1,
      y: 0,
      transition: {
        duration: 0.6,
        ease: [0.25, 0.46, 0.45, 0.94]
      }
    }
  };

  return (
    <section 
      ref={heroRef}
      id="home" 
      className="relative min-h-screen flex items-center justify-center overflow-hidden"
    >
      {/* Interactive Background */}
      <div 
        className="absolute inset-0 opacity-30"
        style={{
          background: `radial-gradient(circle at ${mousePosition.x}% ${mousePosition.y}%, rgba(37, 99, 235, 0.1) 0%, transparent 50%)`
        }}
      />

      {/* Floating Elements */}
      <div className="absolute inset-0 overflow-hidden pointer-events-none">
        {[...Array(6)].map((_, i) => (
          <motion.div
            key={i}
            className="absolute opacity-20"
            style={{
              left: `${20 + i * 15}%`,
              top: `${20 + (i % 3) * 30}%`,
              width: `${60 + (i % 3) * 40}px`,
              height: `${60 + (i % 3) * 40}px`,
            }}
            animate={{
              y: [-20, 20, -20],
              x: [-10, 10, -10],
              rotate: [0, 180, 360],
              scale: [0.8, 1.2, 0.8],
            }}
            transition={{
              duration: 10 + i * 2,
              repeat: Infinity,
              ease: "easeInOut",
              delay: i * 0.5,
            }}
          >
            <div 
              className="w-full h-full rounded-full"
              style={{
                background: i % 3 === 0 
                  ? 'linear-gradient(135deg, rgba(37, 99, 235, 0.3), rgba(147, 197, 253, 0.1))'
                  : i % 3 === 1
                  ? 'linear-gradient(135deg, rgba(124, 58, 237, 0.3), rgba(196, 181, 253, 0.1))'
                  : 'linear-gradient(135deg, rgba(13, 148, 136, 0.3), rgba(167, 243, 208, 0.1))'
              }}
            />
          </motion.div>
        ))}
      </div>

      {/* Main Content */}
      <motion.div
        className="relative z-10 text-center px-4 sm:px-6 lg:px-8"
        style={{ y, opacity }}
      >
        <div className="max-w-4xl mx-auto">
          
          {/* Greeting */}
          <motion.div
            className="mb-6"
            variants={containerVariants}
            initial="hidden"
            animate="visible"
          >
            <motion.p 
              className="text-lg md:text-xl text-gray-600 font-medium"
              variants={itemVariants}
            >
              Hi, I'm
            </motion.p>
          </motion.div>

          {/* Main Title */}
          <motion.div
            className="mb-8"
            variants={titleVariants}
            initial="hidden"
            animate="visible"
          >
            <div className="flex flex-wrap justify-center leading-none">
              {"Rabeya Khatun".split('').map((letter, index) => (
                <motion.span
                  key={index}
                  className="heading-primary inline-block"
                  variants={letterVariants}
                  whileHover={{
                    scale: 1.1,
                    rotate: [0, -5, 5, 0],
                    transition: { duration: 0.3 }
                  }}
                >
                  {letter === ' ' ? '\u00A0' : letter}
                </motion.span>
              ))}
            </div>
          </motion.div>

          {/* Subtitle */}
          <motion.div
            className="mb-12"
            variants={containerVariants}
            initial="hidden"
            animate="visible"
          >
            <motion.h2 
              className="text-xl md:text-2xl lg:text-3xl font-semibold text-gray-700 mb-4"
              variants={itemVariants}
            >
              MERN Stack Developer
            </motion.h2>
            <motion.p 
              className="text-large max-w-2xl mx-auto"
              variants={itemVariants}
            >
              Crafting modern web applications with passion for clean code, 
              innovative solutions, and exceptional user experiences.
            </motion.p>
          </motion.div>

          {/* CTA Buttons */}
          <motion.div
            className="flex flex-col sm:flex-row gap-4 justify-center items-center"
            variants={containerVariants}
            initial="hidden"
            animate="visible"
          >
            <motion.button
              className="btn-primary cursor-hover"
              variants={itemVariants}
              whileHover={{ scale: 1.05 }}
              whileTap={{ scale: 0.95 }}
              onClick={() => {
                const element = document.querySelector('#featured-projects');
                if (element) element.scrollIntoView({ behavior: 'smooth' });
              }}
            >
              View My Work
            </motion.button>
            
            <motion.button
              className="btn-secondary cursor-hover"
              variants={itemVariants}
              whileHover={{ scale: 1.05 }}
              whileTap={{ scale: 0.95 }}
              onClick={() => {
                const element = document.querySelector('#contact');
                if (element) element.scrollIntoView({ behavior: 'smooth' });
              }}
            >
              Get In Touch
            </motion.button>
          </motion.div>

          {/* Scroll Indicator */}
          <motion.div
            className="absolute bottom-8 left-1/2 transform -translate-x-1/2"
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ delay: 2, duration: 0.6 }}
          >
            <motion.div
              className="w-6 h-10 border-2 border-gray-400 rounded-full flex justify-center"
              animate={{ y: [0, 5, 0] }}
              transition={{ duration: 1.5, repeat: Infinity, ease: "easeInOut" }}
            >
              <motion.div
                className="w-1 h-3 bg-gradient-to-b from-blue-600 to-purple-600 rounded-full mt-2"
                animate={{ y: [0, 12, 0] }}
                transition={{ duration: 1.5, repeat: Infinity, ease: "easeInOut" }}
              />
            </motion.div>
            <p className="text-xs text-gray-500 mt-2">Scroll Down</p>
          </motion.div>
        </div>
      </motion.div>

      {/* Profile Image Placeholder */}
      <motion.div
        className="absolute right-8 top-1/2 transform -translate-y-1/2 hidden xl:block"
        initial={{ opacity: 0, x: 100, scale: 0.8 }}
        animate={{ opacity: 1, x: 0, scale: 1 }}
        transition={{ delay: 1, duration: 0.8 }}
      >
        <div className="relative">
          <motion.div
            className="w-80 h-80 bg-gradient-to-br from-blue-100 to-purple-100 rounded-full flex items-center justify-center border-4 border-white shadow-2xl"
            animate={{ rotate: [0, 5, -5, 0] }}
            transition={{ duration: 6, repeat: Infinity, ease: "easeInOut" }}
          >
            <div className="w-72 h-72 bg-gradient-to-br from-gray-200 to-gray-300 rounded-full flex items-center justify-center">
              <span className="text-6xl text-gray-500">üë©‚Äçüíª</span>
            </div>
          </motion.div>
          
          {/* Floating Tech Icons */}
          {['React', 'Node', 'JS', 'CSS'].map((tech, index) => (
            <motion.div
              key={tech}
              className="absolute w-12 h-12 bg-white rounded-full shadow-lg flex items-center justify-center text-sm font-bold text-gray-700"
              style={{
                top: `${20 + index * 20}%`,
                left: index % 2 === 0 ? '-10%' : '110%',
              }}
              animate={{
                y: [-5, 5, -5],
                rotate: [0, 10, -10, 0],
              }}
              transition={{
                duration: 3 + index,
                repeat: Infinity,
                ease: "easeInOut",
                delay: index * 0.5,
              }}
            >
              {tech}
            </motion.div>
          ))}
        </div>
      </motion.div>
    </section>
  );
};

export default Hero;
