import React, { useState, useEffect, useMemo } from 'react';
import { motion, AnimatePresence } from 'framer-motion';

const Navbar = () => {
  const [isOpen, setIsOpen] = useState(false);
  const [activeSection, setActiveSection] = useState('home');
  const [scrolled, setScrolled] = useState(false);

  const menuItems = useMemo(() => [
    { name: 'Home', href: '#home', id: 'home' },
    { name: 'About', href: '#about', id: 'about' },
    { name: 'Skills', href: '#skills', id: 'skills' },
    { name: 'Projects', href: '#featured-projects', id: 'featured-projects' },
    { name: 'Blog', href: '#blog', id: 'blog' },
    { name: 'Contact', href: '#contact', id: 'contact' },
  ], []);

  useEffect(() => {
    const handleScroll = () => {
      setScrolled(window.scrollY > 50);
      
      // Update active section based on scroll position
      const sections = menuItems.map(item => document.getElementById(item.id)).filter(Boolean);
      const scrollPosition = window.scrollY + 100;
      
      for (let i = sections.length - 1; i >= 0; i--) {
        const section = sections[i];
        if (section && section.offsetTop <= scrollPosition) {
          setActiveSection(menuItems[i].id);
          break;
        }
      }
    };

    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, [menuItems]);

  const handleNavClick = (href, id) => {
    setActiveSection(id);
    setIsOpen(false);
    
    // Smooth scroll to section
    const element = document.querySelector(href);
    if (element) {
      element.scrollIntoView({ behavior: 'smooth' });
    }
  };

  return (
    <>
      {/* Continuous Wave Background Animation */}
      <div className="fixed top-0 left-0 right-0 z-40 h-20 overflow-hidden pointer-events-none">
        <svg
          className="absolute inset-0 w-full h-full"
          viewBox="0 0 1200 120"
          preserveAspectRatio="none"
        >
          <defs>
            {/* Unified Theme Gradient - Blue, Purple, Teal */}
            <linearGradient id="waveGradient1" x1="0%" y1="0%" x2="100%" y2="0%">
              <stop offset="0%" stopColor="rgba(37, 99, 235, 0.12)" /> {/* Blue */}
              <stop offset="50%" stopColor="rgba(124, 58, 237, 0.15)" /> {/* Purple */}
              <stop offset="100%" stopColor="rgba(13, 148, 136, 0.12)" /> {/* Teal */}
            </linearGradient>
            
            <linearGradient id="waveGradient2" x1="0%" y1="0%" x2="100%" y2="0%">
              <stop offset="0%" stopColor="rgba(124, 58, 237, 0.08)" /> {/* Purple */}
              <stop offset="50%" stopColor="rgba(13, 148, 136, 0.10)" /> {/* Teal */}
              <stop offset="100%" stopColor="rgba(37, 99, 235, 0.08)" /> {/* Blue */}
            </linearGradient>

            <linearGradient id="waveGradient3" x1="0%" y1="0%" x2="100%" y2="0%">
              <stop offset="0%" stopColor="rgba(13, 148, 136, 0.06)" /> {/* Teal */}
              <stop offset="100%" stopColor="rgba(37, 99, 235, 0.10)" /> {/* Blue */}
            </linearGradient>
          </defs>
          
          {/* Smooth Flowing Wave Layers */}
          <motion.path
            d="M0,60 C400,20 800,100 1200,60 L1200,0 L0,0 Z"
            fill="url(#waveGradient1)"
            animate={{
              d: [
                "M0,60 C400,20 800,100 1200,60 L1200,0 L0,0 Z",
                "M0,80 C400,40 800,80 1200,40 L1200,0 L0,0 Z",
                "M0,50 C400,90 800,30 1200,70 L1200,0 L0,0 Z",
                "M0,60 C400,20 800,100 1200,60 L1200,0 L0,0 Z"
              ]
            }}
            transition={{
              duration: scrolled ? 16 : 12,
              repeat: Infinity,
              ease: "easeInOut"
            }}
          />
          
          <motion.path
            d="M0,40 C300,80 900,20 1200,50 L1200,0 L0,0 Z"
            fill="url(#waveGradient2)"
            animate={{
              d: [
                "M0,40 C300,80 900,20 1200,50 L1200,0 L0,0 Z",
                "M0,20 C300,60 900,40 1200,30 L1200,0 L0,0 Z",
                "M0,70 C300,30 900,70 1200,60 L1200,0 L0,0 Z",
                "M0,40 C300,80 900,20 1200,50 L1200,0 L0,0 Z"
              ]
            }}
            transition={{
              duration: scrolled ? 20 : 15,
              repeat: Infinity,
              ease: "easeInOut",
              delay: -3
            }}
          />

          <motion.path
            d="M0,30 C600,70 600,10 1200,40 L1200,0 L0,0 Z"
            fill="url(#waveGradient3)"
            animate={{
              d: [
                "M0,30 C600,70 600,10 1200,40 L1200,0 L0,0 Z",
                "M0,50 C600,10 600,60 1200,20 L1200,0 L0,0 Z",
                "M0,20 C600,50 600,30 1200,50 L1200,0 L0,0 Z",
                "M0,30 C600,70 600,10 1200,40 L1200,0 L0,0 Z"
              ]
            }}
            transition={{
              duration: scrolled ? 24 : 18,
              repeat: Infinity,
              ease: "easeInOut",
              delay: -6
            }}
          />
        </svg>
      </div>

      <motion.nav
        className={`fixed top-0 left-0 right-0 z-50 transition-all duration-500 ${
          scrolled 
            ? 'bg-white/20 backdrop-blur-xl shadow-xl border-b border-white/10' 
            : 'bg-white/10 backdrop-blur-lg'
        }`}
        initial={{ y: -100, opacity: 0 }}
        animate={{ y: 0, opacity: 1 }}
        transition={{ duration: 0.8, ease: [0.25, 0.46, 0.45, 0.94] }}
      >
        {/* Subtle Floating Particles */}
        <div className="absolute inset-0 overflow-hidden pointer-events-none">
          {[...Array(6)].map((_, i) => (
            <motion.div
              key={i}
              className="absolute w-1.5 h-1.5 rounded-full opacity-20"
              style={{
                left: `${15 + i * 14}%`,
                top: '50%',
                background: i % 3 === 0 
                  ? 'var(--primary-blue)'
                  : i % 3 === 1
                  ? 'var(--primary-purple)'
                  : 'var(--primary-teal)'
              }}
              animate={{
                y: [-8, 8, -8],
                x: [-4, 4, -4],
                scale: [0.5, 1, 0.5],
                opacity: [0.1, 0.3, 0.1]
              }}
              transition={{
                duration: 8 + i * 2,
                repeat: Infinity,
                ease: "easeInOut",
                delay: i * 0.8
              }}
            />
          ))}
        </div>

        <div className="container relative z-10">
          <div className="flex items-center justify-between h-20">
            
            {/* Logo with Gentle Float Animation */}
            <motion.div
              className="flex items-center space-x-3 cursor-pointer group"
              initial={{ scale: 1 }}
              animate={{ 
                y: [-1, 1, -1],
              }}
              transition={{ 
                duration: 6, 
                repeat: Infinity, 
                ease: "easeInOut" 
              }}
              onClick={() => handleNavClick('#home', 'home')}
              whileHover={{ scale: 1.05 }}
              whileTap={{ scale: 0.95 }}
            >
              {/* Clean Logo Design */}
              <motion.div
                className="relative w-11 h-11 bg-gradient-to-br from-blue-500 via-purple-500 to-teal-500 rounded-xl flex items-center justify-center shadow-lg"
                whileHover={{
                  rotate: [0, 5, -5, 0],
                  scale: 1.1
                }}
                transition={{ duration: 0.6 }}
              >
                {/* Subtle Inner Glow */}
                <div className="absolute inset-1 bg-white/20 rounded-lg backdrop-blur-sm" />
                <span className="relative z-10 text-white font-bold text-lg">R</span>
              </motion.div>
              
              <div className="hidden sm:block">
                <h1 className="text-xl font-bold text-gradient">Rabeya Khatun</h1>
                <p className="text-xs text-secondary">MERN Stack Developer</p>
              </div>
            </motion.div>

            {/* Desktop Menu with Wave Interactions */}
            <div className="hidden lg:flex items-center space-x-1">
              {menuItems.map((item, index) => (
                <motion.div
                  key={item.id}
                  className="relative"
                  initial={{ opacity: 0, y: -20 }}
                  animate={{ opacity: 1, y: 0 }}
                  transition={{ delay: index * 0.1 + 0.3 }}
                >
                  <motion.button
                    className={`relative px-5 py-2.5 rounded-xl font-medium transition-all duration-300 overflow-hidden group ${
                      activeSection === item.id
                        ? 'text-white'
                        : 'text-primary hover:text-white'
                    }`}
                    onClick={() => handleNavClick(item.href, item.id)}
                    whileHover={{ 
                      scale: 1.05,
                      y: -2
                    }}
                    whileTap={{ scale: 0.95 }}
                  >
                    {/* Smooth Background Transition */}
                    <motion.div
                      className={`absolute inset-0 bg-gradient-to-r from-blue-500 via-purple-500 to-teal-500 rounded-xl transition-all duration-300 ${
                        activeSection === item.id ? 'opacity-100' : 'opacity-0 group-hover:opacity-80'
                      }`}
                      whileHover={{
                        scale: [1, 1.1, 1]
                      }}
                      transition={{ duration: 0.4 }}
                    />
                    
                    {/* Flowing Wave Underline */}
                    {activeSection === item.id && (
                      <motion.div
                        className="absolute bottom-0 left-0 right-0 h-0.5 bg-gradient-to-r from-blue-300 via-purple-300 to-teal-300 rounded-full"
                        layoutId="activeWave"
                        animate={{
                          scaleX: [1, 1.2, 1],
                          opacity: [0.6, 1, 0.6]
                        }}
                        transition={{
                          duration: 3,
                          repeat: Infinity,
                          ease: "easeInOut"
                        }}
                      />
                    )}
                    
                    <span className="relative z-10">{item.name}</span>
                  </motion.button>
                </motion.div>
              ))}
            </div>

            {/* CTA Button with Animated Border */}
            <div className="hidden lg:block">
              <motion.button
                className="group relative px-6 py-3 bg-gradient-to-r from-blue-500 via-purple-500 to-teal-500 text-white font-semibold rounded-xl overflow-hidden shadow-lg"
                onClick={() => handleNavClick('#contact', 'contact')}
                whileHover={{ 
                  scale: 1.05,
                  boxShadow: "0 20px 40px rgba(37, 99, 235, 0.25)"
                }}
                whileTap={{ scale: 0.95 }}
                initial={{ opacity: 0, x: 20 }}
                animate={{ opacity: 1, x: 0 }}
                transition={{ delay: 0.8 }}
              >
                {/* Ripple Effect on Hover */}
                <motion.div
                  className="absolute inset-0 bg-white/20 rounded-xl scale-0 group-hover:scale-150 opacity-0 group-hover:opacity-100"
                  transition={{ duration: 0.6 }}
                />
                
                <span className="relative z-10 flex items-center">
                  Hire Me
                  <motion.svg 
                    className="w-4 h-4 ml-2" 
                    fill="none" 
                    stroke="currentColor" 
                    viewBox="0 0 24 24"
                    animate={{ x: [0, 2, 0] }}
                    transition={{ duration: 2, repeat: Infinity }}
                  >
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M17 8l4 4m0 0l-4 4m4-4H3" />
                  </motion.svg>
                </span>
              </motion.button>
            </div>

            {/* Mobile Menu Button */}
            <motion.button
              className="lg:hidden relative p-3 rounded-xl bg-white/10 backdrop-blur-sm border border-white/20 hover:bg-white/20 transition-all duration-300"
              onClick={() => setIsOpen(!isOpen)}
              whileTap={{ scale: 0.95 }}
              whileHover={{ scale: 1.05 }}
            >
              <div className="w-6 h-6 flex flex-col justify-center items-center">
                <motion.span
                  className="block h-0.5 w-6 bg-primary rounded-full"
                  animate={{
                    rotate: isOpen ? 45 : 0,
                    y: isOpen ? 6 : 0,
                  }}
                  transition={{ duration: 0.3 }}
                />
                <motion.span
                  className="block h-0.5 w-6 bg-primary rounded-full mt-1.5"
                  animate={{
                    opacity: isOpen ? 0 : 1,
                    scale: isOpen ? 0 : 1
                  }}
                  transition={{ duration: 0.3 }}
                />
                <motion.span
                  className="block h-0.5 w-6 bg-primary rounded-full mt-1.5"
                  animate={{
                    rotate: isOpen ? -45 : 0,
                    y: isOpen ? -6 : 0,
                  }}
                  transition={{ duration: 0.3 }}
                />
              </div>
            </motion.button>
          </div>
        </div>

        {/* Mobile Menu with Smooth Animation */}
        <AnimatePresence>
          {isOpen && (
            <motion.div
              className="lg:hidden absolute top-full left-0 right-0 bg-white/95 backdrop-blur-xl border-t border-white/20 shadow-xl"
              initial={{ opacity: 0, height: 0, y: -20 }}
              animate={{ opacity: 1, height: 'auto', y: 0 }}
              exit={{ opacity: 0, height: 0, y: -20 }}
              transition={{ duration: 0.4, ease: [0.25, 0.46, 0.45, 0.94] }}
            >
              {/* Mobile Menu Wave Background */}
              <div className="absolute inset-0 overflow-hidden">
                <motion.div
                  className="absolute -top-8 left-0 right-0 h-16 bg-gradient-to-r from-blue-100/40 via-purple-100/40 to-teal-100/40 rounded-b-[50%]"
                  animate={{
                    scaleY: [1, 1.1, 1],
                    opacity: [0.4, 0.6, 0.4]
                  }}
                  transition={{
                    duration: 4,
                    repeat: Infinity,
                    ease: "easeInOut"
                  }}
                />
              </div>

              <div className="container py-6 relative z-10">
                <div className="flex flex-col space-y-2">
                  {menuItems.map((item, index) => (
                    <motion.button
                      key={item.id}
                      className={`text-left px-6 py-4 rounded-xl font-medium transition-all duration-300 ${
                        activeSection === item.id
                          ? 'text-white bg-gradient-to-r from-blue-500 via-purple-500 to-teal-500'
                          : 'text-primary hover:text-white hover:bg-gradient-to-r hover:from-blue-400 hover:to-teal-400'
                      }`}
                      onClick={() => handleNavClick(item.href, item.id)}
                      initial={{ opacity: 0, x: -30 }}
                      animate={{ opacity: 1, x: 0 }}
                      transition={{ delay: index * 0.1 }}
                      whileTap={{ scale: 0.98 }}
                    >
                      {item.name}
                    </motion.button>
                  ))}
                  
                  <motion.button
                    className="btn btn-primary mt-4"
                    onClick={() => handleNavClick('#contact', 'contact')}
                    initial={{ opacity: 0, y: 20 }}
                    animate={{ opacity: 1, y: 0 }}
                    transition={{ delay: 0.6 }}
                  >
                    Hire Me
                  </motion.button>
                </div>
              </div>
            </motion.div>
          )}
        </AnimatePresence>
      </motion.nav>
    </>
  );
};

export default Navbar;
