import React, { useState, useEffect, useMemo } from 'react';
import { motion, AnimatePresence } from 'framer-motion';

const Navbar = () => {
  const [isOpen, setIsOpen] = useState(false);
  const [activeSection, setActiveSection] = useState('home');
  const [scrolled, setScrolled] = useState(false);

  const menuItems = useMemo(() => [
    { name: 'Home', href: '#home', id: 'home' },
    { name: 'About', href: '#about', id: 'about' },
    { name: 'Projects', href: '#featured-projects', id: 'featured-projects' },
    { name: 'Contact', href: '#contact', id: 'contact' },
  ], []);

  useEffect(() => {
    const handleScroll = () => {
      setScrolled(window.scrollY > 50);
      
      // Update active section based on scroll position
      const sections = menuItems.map(item => document.getElementById(item.id)).filter(Boolean);
      const scrollPosition = window.scrollY + 100;
      
      for (let i = sections.length - 1; i >= 0; i--) {
        const section = sections[i];
        if (section && section.offsetTop <= scrollPosition) {
          setActiveSection(menuItems[i].id);
          break;
        }
      }
    };

    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, [menuItems]);

  const handleNavClick = (href, id) => {
    setActiveSection(id);
    setIsOpen(false);
    
    // Smooth scroll to section
    const element = document.querySelector(href);
    if (element) {
      element.scrollIntoView({ behavior: 'smooth' });
    }
  };

  return (
    <>
      {/* Animated Wave Background with Unique Color Palette */}
      <div className="fixed top-0 left-0 right-0 z-40 h-20 overflow-hidden pointer-events-none">
        <svg
          className="absolute inset-0 w-full h-full"
          viewBox="0 0 1200 120"
          preserveAspectRatio="none"
        >
          <defs>
            {/* Unique Gradient Palette: Deep Teal, Coral, Soft Cyan, Muted Gold, Midnight Green */}
            <linearGradient id="waveGradient1" x1="0%" y1="0%" x2="100%" y2="0%">
              <stop offset="0%" stopColor="rgba(20, 83, 75, 0.15)" /> {/* Deep Teal */}
              <stop offset="25%" stopColor="rgba(255, 127, 80, 0.12)" /> {/* Coral */}
              <stop offset="50%" stopColor="rgba(125, 211, 252, 0.18)" /> {/* Soft Cyan */}
              <stop offset="75%" stopColor="rgba(217, 179, 140, 0.14)" /> {/* Muted Gold */}
              <stop offset="100%" stopColor="rgba(6, 78, 59, 0.16)" /> {/* Midnight Green */}
            </linearGradient>
            
            <linearGradient id="waveGradient2" x1="0%" y1="0%" x2="100%" y2="0%">
              <stop offset="0%" stopColor="rgba(255, 127, 80, 0.08)" /> {/* Coral */}
              <stop offset="33%" stopColor="rgba(125, 211, 252, 0.12)" /> {/* Soft Cyan */}
              <stop offset="66%" stopColor="rgba(6, 78, 59, 0.10)" /> {/* Midnight Green */}
              <stop offset="100%" stopColor="rgba(20, 83, 75, 0.14)" /> {/* Deep Teal */}
            </linearGradient>

            <linearGradient id="waveGradient3" x1="0%" y1="0%" x2="100%" y2="0%">
              <stop offset="0%" stopColor="rgba(217, 179, 140, 0.10)" /> {/* Muted Gold */}
              <stop offset="50%" stopColor="rgba(20, 83, 75, 0.12)" /> {/* Deep Teal */}
              <stop offset="100%" stopColor="rgba(255, 127, 80, 0.08)" /> {/* Coral */}
            </linearGradient>
          </defs>
          
          {/* Multiple Wave Layers with Organic Motion */}
          <motion.path
            d="M0,60 C300,20 600,100 900,60 C1050,40 1150,80 1200,60 L1200,0 L0,0 Z"
            fill="url(#waveGradient1)"
            animate={{
              d: [
                "M0,60 C300,20 600,100 900,60 C1050,40 1150,80 1200,60 L1200,0 L0,0 Z",
                "M0,80 C300,40 600,80 900,40 C1050,60 1150,60 1200,80 L1200,0 L0,0 Z",
                "M0,50 C300,90 600,30 900,70 C1050,20 1150,90 1200,50 L1200,0 L0,0 Z",
                "M0,60 C300,20 600,100 900,60 C1050,40 1150,80 1200,60 L1200,0 L0,0 Z"
              ]
            }}
            transition={{
              duration: 12,
              repeat: Infinity,
              ease: "easeInOut"
            }}
          />
          
          <motion.path
            d="M0,40 C300,80 600,20 900,40 C1050,60 1150,20 1200,40 L1200,0 L0,0 Z"
            fill="url(#waveGradient2)"
            animate={{
              d: [
                "M0,40 C300,80 600,20 900,40 C1050,60 1150,20 1200,40 L1200,0 L0,0 Z",
                "M0,20 C300,60 600,40 900,20 C1050,40 1150,40 1200,20 L1200,0 L0,0 Z",
                "M0,70 C300,30 600,70 900,50 C1050,80 1150,10 1200,70 L1200,0 L0,0 Z",
                "M0,40 C300,80 600,20 900,40 C1050,60 1150,20 1200,40 L1200,0 L0,0 Z"
              ]
            }}
            transition={{
              duration: 16,
              repeat: Infinity,
              ease: "easeInOut",
              delay: -3
            }}
          />

          <motion.path
            d="M0,30 C300,70 600,10 900,50 C1050,30 1150,70 1200,30 L1200,0 L0,0 Z"
            fill="url(#waveGradient3)"
            animate={{
              d: [
                "M0,30 C300,70 600,10 900,50 C1050,30 1150,70 1200,30 L1200,0 L0,0 Z",
                "M0,50 C300,10 600,60 900,20 C1050,70 1150,30 1200,50 L1200,0 L0,0 Z",
                "M0,20 C300,50 600,30 900,60 C1050,10 1150,50 1200,20 L1200,0 L0,0 Z",
                "M0,30 C300,70 600,10 900,50 C1050,30 1150,70 1200,30 L1200,0 L0,0 Z"
              ]
            }}
            transition={{
              duration: 20,
              repeat: Infinity,
              ease: "easeInOut",
              delay: -6
            }}
          />
        </svg>
      </div>

      <motion.nav
        className={`fixed top-0 left-0 right-0 z-50 transition-all duration-500 ${
          scrolled 
            ? 'bg-white/15 backdrop-blur-xl shadow-2xl border-b border-white/10' 
            : 'bg-white/8 backdrop-blur-md'
        }`}
        initial={{ y: -100, opacity: 0 }}
        animate={{ y: 0, opacity: 1 }}
        transition={{ duration: 0.8, ease: [0.25, 0.46, 0.45, 0.94] }}
      >
        {/* Floating Particles with Unique Colors */}
        <div className="absolute inset-0 overflow-hidden pointer-events-none">
          {[...Array(8)].map((_, i) => (
            <motion.div
              key={i}
              className="absolute rounded-full opacity-25"
              style={{
                left: `${12 + i * 12}%`,
                top: '50%',
                width: `${3 + (i % 2)}px`,
                height: `${3 + (i % 2)}px`,
                background: i % 5 === 0 
                  ? 'linear-gradient(135deg, #14534b, #0f766e)' // Deep Teal
                  : i % 5 === 1
                  ? 'linear-gradient(135deg, #ff7f50, #ff6347)' // Coral
                  : i % 5 === 2
                  ? 'linear-gradient(135deg, #7dd3fc, #38bdf8)' // Soft Cyan
                  : i % 5 === 3
                  ? 'linear-gradient(135deg, #d9b38c, #c4a574)' // Muted Gold
                  : 'linear-gradient(135deg, #064e3b, #065f46)' // Midnight Green
              }}
              animate={{
                y: [-12, 12, -12],
                x: [-6, 6, -6],
                scale: [0.6, 1.4, 0.6],
                opacity: [0.2, 0.6, 0.2]
              }}
              transition={{
                duration: 6 + i * 0.8,
                repeat: Infinity,
                ease: "easeInOut",
                delay: i * 0.4
              }}
            />
          ))}
        </div>

        <div className="container-max relative z-10">
          <div className="flex items-center justify-between h-20">
            
            {/* Logo with Organic Floating Animation */}
            <motion.div
              className="flex items-center space-x-3 cursor-pointer group"
              initial={{ scale: 1 }}
              animate={{ 
                y: [-3, 3, -3],
                scale: [1, 1.03, 1] 
              }}
              transition={{ 
                duration: 5, 
                repeat: Infinity, 
                ease: "easeInOut" 
              }}
              onClick={() => handleNavClick('#home', 'home')}
              whileHover={{ scale: 1.08, rotate: [0, 2, -2, 0] }}
              whileTap={{ scale: 0.95 }}
            >
              {/* Morphing Logo Container with Unique Gradient */}
              <motion.div
                className="relative w-12 h-12 bg-gradient-to-br from-teal-600 via-orange-400 to-cyan-400 flex items-center justify-center shadow-xl overflow-hidden"
                animate={{
                  borderRadius: [
                    "25% 75% 60% 40% / 35% 25% 75% 65%",
                    "75% 25% 40% 60% / 65% 75% 25% 35%",
                    "40% 60% 75% 25% / 50% 60% 40% 50%",
                    "25% 75% 60% 40% / 35% 25% 75% 65%"
                  ]
                }}
                transition={{
                  duration: 8,
                  repeat: Infinity,
                  ease: "easeInOut"
                }}
              >
                {/* Rotating Inner Glow with Gold Accent */}
                <motion.div
                  className="absolute inset-1 bg-gradient-to-br from-yellow-200/30 via-white/20 to-cyan-200/30 backdrop-blur-sm"
                  animate={{
                    borderRadius: [
                      "75% 25% 40% 60% / 65% 75% 25% 35%",
                      "25% 75% 60% 40% / 35% 25% 75% 65%",
                      "60% 40% 25% 75% / 40% 50% 60% 50%",
                      "75% 25% 40% 60% / 65% 75% 25% 35%"
                    ],
                    rotate: [0, 120, 240, 360]
                  }}
                  transition={{
                    duration: 10,
                    repeat: Infinity,
                    ease: "linear"
                  }}
                />
                
                {/* Pulsing Center */}
                <motion.div
                  className="relative z-10 w-8 h-8 bg-gradient-to-br from-white to-cyan-100 rounded-full flex items-center justify-center"
                  animate={{
                    scale: [1, 1.1, 1],
                    opacity: [0.9, 1, 0.9]
                  }}
                  transition={{
                    duration: 3,
                    repeat: Infinity,
                    ease: "easeInOut"
                  }}
                >
                  <span className="text-teal-800 font-bold text-lg">R</span>
                </motion.div>
              </motion.div>
              
              <div className="hidden sm:block">
                <motion.h1 
                  className="text-xl font-bold bg-gradient-to-r from-teal-700 via-orange-600 to-cyan-600 bg-clip-text text-transparent"
                  animate={{
                    backgroundPosition: ['0% 50%', '100% 50%', '0% 50%']
                  }}
                  transition={{
                    duration: 6,
                    repeat: Infinity,
                    ease: "easeInOut"
                  }}
                >
                  Rabeya Khatun
                </motion.h1>
                <p className="text-xs text-teal-700/80 font-medium">MERN Stack Developer</p>
              </div>
            </motion.div>

            {/* Desktop Menu with Wave Interactions */}
            <div className="hidden lg:flex items-center space-x-2">
              {menuItems.map((item, index) => (
                <motion.div
                  key={item.id}
                  className="relative"
                  initial={{ opacity: 0, y: -20 }}
                  animate={{ opacity: 1, y: 0 }}
                  transition={{ delay: index * 0.1 + 0.3 }}
                >
                  <motion.button
                    className={`relative px-6 py-3 rounded-2xl font-medium transition-all duration-300 overflow-hidden group ${
                      activeSection === item.id
                        ? 'text-white shadow-lg'
                        : 'text-teal-800 hover:text-white'
                    }`}
                    onClick={() => handleNavClick(item.href, item.id)}
                    whileHover={{ 
                      scale: 1.05,
                      y: -3
                    }}
                    whileTap={{ scale: 0.95 }}
                  >
                    {/* Ripple Effect Background with Unique Gradient */}
                    <motion.div
                      className={`absolute inset-0 bg-gradient-to-r from-teal-500 via-orange-400 to-cyan-500 rounded-2xl transition-all duration-300 ${
                        activeSection === item.id ? 'opacity-100' : 'opacity-0 group-hover:opacity-90'
                      }`}
                      whileHover={{
                        scale: [1, 1.15, 1],
                        rotate: [0, 3, -3, 0]
                      }}
                      transition={{ duration: 0.8 }}
                    />
                    
                    {/* Floating Wave Underline */}
                    {activeSection === item.id && (
                      <motion.div
                        className="absolute bottom-0 left-0 right-0 h-1 bg-gradient-to-r from-yellow-300 via-orange-300 to-cyan-300 rounded-full"
                        layoutId="activeWave"
                        animate={{
                          scaleX: [1, 1.2, 1],
                          opacity: [0.7, 1, 0.7],
                          y: [0, -1, 0]
                        }}
                        transition={{
                          duration: 2.5,
                          repeat: Infinity,
                          ease: "easeInOut"
                        }}
                      />
                    )}
                    
                    <span className="relative z-10">{item.name}</span>
                  </motion.button>
                </motion.div>
              ))}
            </div>

            {/* CTA Button with Animated Wave Border */}
            <div className="hidden lg:block">
              <motion.button
                className="group relative px-6 py-3 bg-gradient-to-r from-teal-600 via-orange-500 to-cyan-500 text-white font-semibold rounded-2xl overflow-hidden shadow-xl"
                onClick={() => handleNavClick('#contact', 'contact')}
                whileHover={{ 
                  scale: 1.08,
                  boxShadow: "0 25px 50px rgba(20, 83, 75, 0.4)"
                }}
                whileTap={{ scale: 0.95 }}
                initial={{ opacity: 0, x: 20 }}
                animate={{ opacity: 1, x: 0 }}
                transition={{ delay: 0.8 }}
              >
                {/* Animated Border Wave with Gold Accent */}
                <motion.div
                  className="absolute inset-0 bg-gradient-to-r from-yellow-400 via-orange-400 to-teal-400 rounded-2xl"
                  animate={{
                    rotate: [0, 360],
                    scale: [1, 1.08, 1]
                  }}
                  transition={{
                    duration: 5,
                    repeat: Infinity,
                    ease: "linear"
                  }}
                />
                
                {/* Inner Button with Morphing Background */}
                <motion.div
                  className="relative bg-gradient-to-r from-teal-600 via-orange-500 to-cyan-500 rounded-2xl px-6 py-3 m-0.5"
                  whileHover={{
                    background: "linear-gradient(45deg, #0f766e, #ff7f50, #0891b2)"
                  }}
                  animate={{
                    backgroundPosition: ['0% 50%', '100% 50%', '0% 50%']
                  }}
                  transition={{
                    backgroundPosition: {
                      duration: 4,
                      repeat: Infinity,
                      ease: "easeInOut"
                    }
                  }}
                >
                  <span className="relative z-10 flex items-center">
                    Hire Me
                    <motion.svg 
                      className="w-4 h-4 ml-2" 
                      fill="none" 
                      stroke="currentColor" 
                      viewBox="0 0 24 24"
                      animate={{ x: [0, 4, 0] }}
                      transition={{ duration: 2, repeat: Infinity }}
                    >
                      <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M17 8l4 4m0 0l-4 4m4-4H3" />
                    </motion.svg>
                  </span>
                </motion.div>
              </motion.button>
            </div>

            {/* Mobile Menu Button with Organic Animation */}
            <motion.button
              className="lg:hidden relative p-3 rounded-xl bg-white/15 backdrop-blur-sm border border-white/20 hover:bg-white/25 transition-all duration-300 shadow-lg"
              onClick={() => setIsOpen(!isOpen)}
              whileTap={{ scale: 0.95 }}
              whileHover={{ scale: 1.05, rotate: [0, 2, -2, 0] }}
            >
              <div className="w-6 h-6 flex flex-col justify-center items-center">
                <motion.span
                  className="block h-0.5 w-6 bg-teal-700 rounded-full"
                  animate={{
                    rotate: isOpen ? 45 : 0,
                    y: isOpen ? 6 : 0,
                  }}
                  transition={{ duration: 0.3 }}
                />
                <motion.span
                  className="block h-0.5 w-6 bg-teal-700 rounded-full mt-1.5"
                  animate={{
                    opacity: isOpen ? 0 : 1,
                    scale: isOpen ? 0 : 1
                  }}
                  transition={{ duration: 0.3 }}
                />
                <motion.span
                  className="block h-0.5 w-6 bg-teal-700 rounded-full mt-1.5"
                  animate={{
                    rotate: isOpen ? -45 : 0,
                    y: isOpen ? -6 : 0,
                  }}
                  transition={{ duration: 0.3 }}
                />
              </div>
            </motion.button>
          </div>
        </div>

        {/* Mobile Menu with Organic Wave Animation */}
        <AnimatePresence>
          {isOpen && (
            <motion.div
              className="lg:hidden absolute top-full left-0 right-0 bg-white/90 backdrop-blur-xl border-t border-white/20 shadow-2xl"
              initial={{ opacity: 0, height: 0, y: -20 }}
              animate={{ opacity: 1, height: 'auto', y: 0 }}
              exit={{ opacity: 0, height: 0, y: -20 }}
              transition={{ duration: 0.4, ease: [0.25, 0.46, 0.45, 0.94] }}
            >
              {/* Mobile Menu Wave Background */}
              <div className="absolute inset-0 overflow-hidden">
                <motion.div
                  className="absolute -top-10 left-0 right-0 h-20 bg-gradient-to-r from-teal-100/60 via-orange-100/40 to-cyan-100/60 rounded-b-[60%]"
                  animate={{
                    scaleY: [1, 1.15, 1],
                    opacity: [0.4, 0.7, 0.4],
                    borderRadius: ["60%", "40%", "60%"]
                  }}
                  transition={{
                    duration: 4,
                    repeat: Infinity,
                    ease: "easeInOut"
                  }}
                />
              </div>

              <div className="container-max py-6 relative z-10">
                <div className="flex flex-col space-y-2">
                  {menuItems.map((item, index) => (
                    <motion.button
                      key={item.id}
                      className={`text-left px-6 py-4 rounded-xl font-medium transition-all duration-300 ${
                        activeSection === item.id
                          ? 'text-white bg-gradient-to-r from-teal-500 via-orange-400 to-cyan-500 shadow-lg'
                          : 'text-teal-800 hover:text-white hover:bg-gradient-to-r hover:from-teal-400 hover:to-cyan-400'
                      }`}
                      onClick={() => handleNavClick(item.href, item.id)}
                      initial={{ opacity: 0, x: -30 }}
                      animate={{ opacity: 1, x: 0 }}
                      transition={{ delay: index * 0.1 }}
                      whileTap={{ scale: 0.98 }}
                    >
                      {item.name}
                    </motion.button>
                  ))}
                  
                  <motion.button
                    className="mt-4 px-6 py-4 bg-gradient-to-r from-teal-600 via-orange-500 to-cyan-500 text-white font-semibold rounded-xl shadow-lg"
                    onClick={() => handleNavClick('#contact', 'contact')}
                    initial={{ opacity: 0, y: 20 }}
                    animate={{ opacity: 1, y: 0 }}
                    transition={{ delay: 0.5 }}
                    whileTap={{ scale: 0.98 }}
                  >
                    Hire Me
                  </motion.button>
                </div>
              </div>
            </motion.div>
          )}
        </AnimatePresence>
      </motion.nav>
    </>
  );
};

export default Navbar;
