import React, { useState, useEffect, useRef } from 'react';
import { motion, useScroll, useTransform, useSpring } from 'framer-motion';
import './CinematicEffects.css';

const Hero = () => {
  const [mousePosition, setMousePosition] = useState({ x: 0, y: 0 });
  const [isLoaded, setIsLoaded] = useState(false);
  const heroRef = useRef(null);
  
  const { scrollYProgress } = useScroll({
    target: heroRef,
    offset: ["start start", "end start"]
  });

  const y = useTransform(scrollYProgress, [0, 1], [0, -200]);
  const opacity = useTransform(scrollYProgress, [0, 0.8], [1, 0]);
  const scale = useTransform(scrollYProgress, [0, 1], [1, 0.8]);

  useEffect(() => {
    const handleMouseMove = (e) => {
      setMousePosition({
        x: (e.clientX / window.innerWidth) * 100,
        y: (e.clientY / window.innerHeight) * 100,
      });
    };

    window.addEventListener('mousemove', handleMouseMove);
    setTimeout(() => setIsLoaded(true), 500);
    
    return () => window.removeEventListener('mousemove', handleMouseMove);
  }, []);

  // Title animation variants
  const titleVariants = {
    hidden: { opacity: 0 },
    visible: {
      opacity: 1,
      transition: {
        staggerChildren: 0.08,
        delayChildren: 0.3
      }
    }
  };

  const letterVariants = {
    hidden: { 
      opacity: 0, 
      y: 100,
      rotateX: -90,
      scale: 0.5
    },
    visible: {
      opacity: 1,
      y: 0,
      rotateX: 0,
      scale: 1,
      transition: {
        type: "spring",
        damping: 12,
        stiffness: 200,
        duration: 0.8
      }
    }
  };

  const waveTextVariants = {
    animate: {
      backgroundPosition: ['0% 50%', '100% 50%', '0% 50%'],
      transition: {
        duration: 3,
        repeat: Infinity,
        ease: "easeInOut"
      }
    }
  };

  return (
    <motion.section 
      ref={heroRef}
      id="home" 
      className="relative min-h-screen overflow-hidden"
      style={{ y, opacity, scale }}
    >
      {/* Cinematic Background */}
      <div className="absolute inset-0">
        {/* Base Gradient */}
        <div className="absolute inset-0 bg-gradient-to-br from-slate-950 via-teal-950 to-emerald-950" />
        
        {/* Dynamic Mesh Gradient */}
        <motion.div
          className="absolute inset-0 opacity-60"
          style={{
            background: `
              radial-gradient(circle at ${mousePosition.x}% ${mousePosition.y}%, 
                rgba(20, 184, 166, 0.4) 0%, 
                rgba(6, 182, 212, 0.2) 25%, 
                rgba(249, 115, 22, 0.15) 50%, 
                transparent 70%)
            `
          }}
        />

        {/* Animated Orbs */}
        <div className="absolute inset-0">
          {[...Array(15)].map((_, i) => (
            <motion.div
              key={i}
              className="absolute rounded-full opacity-20 blur-sm"
              style={{
                left: `${10 + (i * 6)}%`,
                top: `${15 + (i % 5) * 18}%`,
                width: `${30 + (i % 4) * 25}px`,
                height: `${30 + (i % 4) * 25}px`,
                background: i % 3 === 0 
                  ? 'linear-gradient(135deg, rgba(20, 184, 166, 0.6), rgba(6, 182, 212, 0.3))'
                  : i % 3 === 1
                  ? 'linear-gradient(135deg, rgba(249, 115, 22, 0.6), rgba(234, 88, 12, 0.3))'
                  : 'linear-gradient(135deg, rgba(6, 182, 212, 0.6), rgba(8, 145, 178, 0.3))'
              }}
              animate={{
                y: [-30, 30, -30],
                x: [-20, 20, -20],
                rotate: [0, 360],
                scale: [0.8, 1.2, 0.8],
              }}
              transition={{
                duration: 12 + i * 0.8,
                repeat: Infinity,
                ease: "easeInOut",
                delay: i * 0.4,
              }}
            />
          ))}
        </div>

        {/* Flowing Wave Patterns */}
        <svg className="absolute inset-0 w-full h-full opacity-30" viewBox="0 0 1200 800" preserveAspectRatio="xMidYMid slice">
          <defs>
            <linearGradient id="waveGrad1" x1="0%" y1="0%" x2="100%" y2="0%">
              <stop offset="0%" stopColor="#14b8a6" stopOpacity="0.3" />
              <stop offset="50%" stopColor="#06b6d4" stopOpacity="0.6" />
              <stop offset="100%" stopColor="#0891b2" stopOpacity="0.3" />
            </linearGradient>
            <linearGradient id="waveGrad2" x1="0%" y1="0%" x2="100%" y2="0%">
              <stop offset="0%" stopColor="#f97316" stopOpacity="0.2" />
              <stop offset="50%" stopColor="#ea580c" stopOpacity="0.4" />
              <stop offset="100%" stopColor="#c2410c" stopOpacity="0.2" />
            </linearGradient>
          </defs>
          
          <motion.path
            d="M0,400 C300,500 900,300 1200,400 L1200,800 L0,800 Z"
            fill="url(#waveGrad1)"
            animate={{
              d: [
                "M0,400 C300,500 900,300 1200,400 L1200,800 L0,800 Z",
                "M0,350 C300,450 900,250 1200,350 L1200,800 L0,800 Z",
                "M0,450 C300,550 900,350 1200,450 L1200,800 L0,800 Z",
                "M0,400 C300,500 900,300 1200,400 L1200,800 L0,800 Z"
              ]
            }}
            transition={{
              duration: 15,
              repeat: Infinity,
              ease: "easeInOut"
            }}
          />
          
          <motion.path
            d="M0,300 C400,400 800,200 1200,300 L1200,800 L0,800 Z"
            fill="url(#waveGrad2)"
            animate={{
              d: [
                "M0,300 C400,400 800,200 1200,300 L1200,800 L0,800 Z",
                "M0,350 C400,450 800,250 1200,350 L1200,800 L0,800 Z",
                "M0,250 C400,350 800,150 1200,250 L1200,800 L0,800 Z",
                "M0,300 C400,400 800,200 1200,300 L1200,800 L0,800 Z"
              ]
            }}
            transition={{
              duration: 10,
              repeat: Infinity,
              ease: "easeInOut",
              delay: 3
            }}
          />
        </svg>

        {/* Particle System */}
        <div className="absolute inset-0 overflow-hidden">
          {[...Array(25)].map((_, i) => (
            <motion.div
              key={`particle-${i}`}
              className="absolute w-1 h-1 bg-cyan-400 rounded-full opacity-40"
              style={{
                left: `${Math.random() * 100}%`,
                top: `${Math.random() * 100}%`,
              }}
              animate={{
                y: [-40, 40, -40],
                x: [-30, 30, -30],
                opacity: [0.2, 0.8, 0.2],
                scale: [0.5, 1.5, 0.5],
              }}
              transition={{
                duration: 8 + Math.random() * 6,
                repeat: Infinity,
                ease: "easeInOut",
                delay: Math.random() * 3,
              }}
            />
          ))}
        </div>
      </div>

      {/* Main Content */}
      <div className="relative z-10 max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 min-h-screen flex items-center pt-20">
        <div className="grid grid-cols-1 lg:grid-cols-12 gap-8 items-center w-full">
          
          {/* Left Column - Text Content */}
          <div className="lg:col-span-7 text-left">
            
            {/* Cinematic Title */}
            <motion.div
              className="mb-8 overflow-hidden"
              variants={titleVariants}
              initial="hidden"
              animate={isLoaded ? "visible" : "hidden"}
            >
              <div className="flex flex-wrap leading-none">
                {"Creative".split('').map((letter, index) => (
                  <motion.span
                    key={index}
                    className="text-6xl md:text-8xl lg:text-9xl font-black inline-block"
                    variants={letterVariants}
                    style={{
                      background: 'linear-gradient(135deg, #ffffff 0%, #06b6d4 50%, #14b8a6 100%)',
                      backgroundSize: '200% 200%',
                      WebkitBackgroundClip: 'text',
                      WebkitTextFillColor: 'transparent',
                      backgroundClip: 'text',
                      textShadow: '0 0 40px rgba(20, 184, 166, 0.5)',
                      filter: 'drop-shadow(0 4px 8px rgba(0,0,0,0.3))'
                    }}
                    whileHover={{
                      scale: 1.1,
                      rotate: [0, -3, 3, 0],
                      transition: { duration: 0.3 }
                    }}
                  >
                    {letter}
                  </motion.span>
                ))}
              </div>
              
              <div className="flex flex-wrap leading-none -mt-4">
                {"Developer".split('').map((letter, index) => (
                  <motion.span
                    key={index + 8}
                    className="text-6xl md:text-8xl lg:text-9xl font-black inline-block"
                    variants={letterVariants}
                    style={{
                      background: 'linear-gradient(135deg, #f97316 0%, #ea580c 50%, #06b6d4 100%)',
                      backgroundSize: '200% 200%',
                      WebkitBackgroundClip: 'text',
                      WebkitTextFillColor: 'transparent',
                      backgroundClip: 'text',
                      textShadow: '0 0 40px rgba(249, 115, 22, 0.5)',
                      filter: 'drop-shadow(0 4px 8px rgba(0,0,0,0.3))'
                    }}
                    whileHover={{
                      scale: 1.1,
                      rotate: [0, 3, -3, 0],
                      transition: { duration: 0.3 }
                    }}
                  >
                    {letter}
                  </motion.span>
                ))}
              </div>
            </motion.div>

            {/* Animated Subtitle */}
            <motion.div
              className="mb-10 overflow-hidden"
              initial={{ opacity: 0, y: 50 }}
              animate={{ opacity: 1, y: 0 }}
              transition={{ duration: 1, delay: 1.5 }}
            >
              <motion.p 
                className="text-xl md:text-2xl lg:text-3xl font-medium leading-relaxed"
                variants={waveTextVariants}
                animate="animate"
                style={{
                  background: 'linear-gradient(90deg, rgba(255,255,255,0.9) 0%, rgba(6,182,212,1) 25%, rgba(20,184,166,1) 50%, rgba(249,115,22,0.9) 75%, rgba(255,255,255,0.9) 100%)',
                  backgroundSize: '300% 100%',
                  WebkitBackgroundClip: 'text',
                  WebkitTextFillColor: 'transparent',
                  backgroundClip: 'text'
                }}
              >
                Hi, I am Rabeya Khatun, a MERN Stack Developer
              </motion.p>
            </motion.div>

            {/* Premium CTA Button */}
            <motion.div
              initial={{ opacity: 0, y: 50 }}
              animate={{ opacity: 1, y: 0 }}
              transition={{ duration: 1, delay: 2 }}
            >
              <motion.button
                className="group relative px-10 py-5 bg-gradient-to-r from-teal-500 via-cyan-500 to-blue-500 text-white font-bold text-lg rounded-full overflow-hidden shadow-2xl"
                whileHover={{ scale: 1.05 }}
                whileTap={{ scale: 0.95 }}
                onClick={() => document.getElementById('projects')?.scrollIntoView({ behavior: 'smooth' })}
              >
                <span className="relative z-10">View My Work</span>
                
                {/* Animated Background */}
                <motion.div
                  className="absolute inset-0 bg-gradient-to-r from-orange-500 via-red-500 to-pink-500"
                  initial={{ x: '-100%' }}
                  whileHover={{ x: 0 }}
                  transition={{ duration: 0.3 }}
                />
                
                {/* Ripple Effect */}
                <motion.div
                  className="absolute inset-0 bg-white opacity-0"
                  whileHover={{
                    opacity: [0, 0.2, 0],
                    scale: [1, 1.05, 1],
                  }}
                  transition={{ duration: 0.6 }}
                />
                
                {/* Glow Effect */}
                <motion.div
                  className="absolute -inset-1 bg-gradient-to-r from-teal-400 to-cyan-400 rounded-full opacity-50 blur-lg"
                  animate={{
                    scale: [1, 1.1, 1],
                    opacity: [0.5, 0.8, 0.5],
                  }}
                  transition={{
                    duration: 2,
                    repeat: Infinity,
                    ease: "easeInOut"
                  }}
                />
              </motion.button>
            </motion.div>

            {/* Floating Decorative Elements */}
            <motion.div
              className="absolute -left-16 top-32 opacity-30"
              animate={{
                y: [-20, 20, -20],
                rotate: [0, 180, 360],
              }}
              transition={{
                duration: 8,
                repeat: Infinity,
                ease: "easeInOut"
              }}
            >
              <div className="w-24 h-24 border-2 border-cyan-400 rounded-full" />
            </motion.div>
            
            <motion.div
              className="absolute -left-8 bottom-32 opacity-25"
              animate={{
                y: [15, -15, 15],
                rotate: [360, 180, 0],
              }}
              transition={{
                duration: 6,
                repeat: Infinity,
                ease: "easeInOut",
                delay: 1
              }}
            >
              <div className="w-16 h-16 bg-gradient-to-r from-orange-400 to-red-400 rounded-lg" />
            </motion.div>
          </div>

          {/* Right Column - Profile Image */}
          <div className="lg:col-span-5 flex justify-center lg:justify-end">
            <motion.div
              className="relative"
              initial={{ opacity: 0, scale: 0.5, rotateY: 90 }}
              animate={{ opacity: 1, scale: 1, rotateY: 0 }}
              transition={{ duration: 1.2, delay: 1.2, ease: "easeOut" }}
            >
              {/* Main Image Container */}
              <motion.div
                className="relative w-80 h-80 md:w-96 md:h-96 lg:w-[28rem] lg:h-[28rem]"
                animate={{
                  y: [-15, 15, -15],
                  rotate: [-1, 1, -1],
                }}
                transition={{
                  duration: 8,
                  repeat: Infinity,
                  ease: "easeInOut"
                }}
              >
                {/* Morphing Blob Background */}
                <motion.div
                  className="absolute inset-0 opacity-30 blur-2xl"
                  animate={{
                    borderRadius: [
                      "60% 40% 30% 70% / 60% 30% 70% 40%",
                      "30% 60% 70% 40% / 50% 60% 30% 60%",
                      "60% 40% 30% 70% / 60% 30% 70% 40%"
                    ],
                    scale: [1, 1.1, 1],
                  }}
                  transition={{
                    duration: 10,
                    repeat: Infinity,
                    ease: "easeInOut"
                  }}
                  style={{
                    background: 'linear-gradient(135deg, rgba(20, 184, 166, 0.6), rgba(6, 182, 212, 0.4), rgba(249, 115, 22, 0.3))'
                  }}
                />
                
                {/* Glass Morphism Frame */}
                <motion.div
                  className="absolute inset-6 backdrop-blur-xl bg-white/5 border border-white/10 overflow-hidden shadow-2xl"
                  style={{
                    borderRadius: "60% 40% 30% 70% / 60% 30% 70% 40%"
                  }}
                  animate={{
                    borderRadius: [
                      "60% 40% 30% 70% / 60% 30% 70% 40%",
                      "30% 60% 70% 40% / 50% 60% 30% 60%",
                      "60% 40% 30% 70% / 60% 30% 70% 40%"
                    ]
                  }}
                  transition={{
                    duration: 10,
                    repeat: Infinity,
                    ease: "easeInOut"
                  }}
                >
                  <img 
                    src="https://via.placeholder.com/500x500/0ea5e9/ffffff?text=RK" 
                    alt="Rabeya Khatun" 
                    className="w-full h-full object-cover"
                  />
                  
                  {/* Dynamic Overlay */}
                  <motion.div
                    className="absolute inset-0"
                    animate={{
                      background: [
                        'linear-gradient(45deg, rgba(20, 184, 166, 0.1) 0%, transparent 50%, rgba(249, 115, 22, 0.1) 100%)',
                        'linear-gradient(135deg, rgba(249, 115, 22, 0.1) 0%, transparent 50%, rgba(6, 182, 212, 0.1) 100%)',
                        'linear-gradient(45deg, rgba(20, 184, 166, 0.1) 0%, transparent 50%, rgba(249, 115, 22, 0.1) 100%)'
                      ]
                    }}
                    transition={{
                      duration: 6,
                      repeat: Infinity,
                      ease: "easeInOut"
                    }}
                  />
                </motion.div>

                {/* Orbiting Particles */}
                {[...Array(12)].map((_, i) => (
                  <motion.div
                    key={i}
                    className="absolute w-2 h-2 rounded-full"
                    style={{
                      background: i % 2 === 0 
                        ? 'linear-gradient(45deg, #14b8a6, #06b6d4)' 
                        : 'linear-gradient(45deg, #f97316, #ea580c)',
                      left: '50%',
                      top: '50%',
                    }}
                    animate={{
                      rotate: [0, 360],
                      x: [0, Math.cos(i * 30 * Math.PI / 180) * 200],
                      y: [0, Math.sin(i * 30 * Math.PI / 180) * 200],
                      opacity: [0.3, 1, 0.3],
                      scale: [0.5, 1, 0.5],
                    }}
                    transition={{
                      duration: 8 + i * 0.5,
                      repeat: Infinity,
                      ease: "linear",
                      delay: i * 0.2,
                    }}
                  />
                ))}

                {/* Rotating Ring */}
                <motion.div
                  className="absolute -inset-4 border-2 border-gradient-to-r from-teal-400 via-transparent to-orange-400 opacity-60"
                  style={{
                    borderRadius: "60% 40% 30% 70% / 60% 30% 70% 40%",
                    background: 'conic-gradient(from 0deg, transparent, rgba(20, 184, 166, 0.3), transparent, rgba(249, 115, 22, 0.3), transparent)'
                  }}
                  animate={{ 
                    rotate: 360,
                    borderRadius: [
                      "60% 40% 30% 70% / 60% 30% 70% 40%",
                      "30% 60% 70% 40% / 50% 60% 30% 60%",
                      "60% 40% 30% 70% / 60% 30% 70% 40%"
                    ]
                  }}
                  transition={{
                    rotate: { duration: 20, repeat: Infinity, ease: "linear" },
                    borderRadius: { duration: 10, repeat: Infinity, ease: "easeInOut" }
                  }}
                />
              </motion.div>

              {/* Ambient Decorative Elements */}
              <motion.div
                className="absolute -top-12 -right-12 w-24 h-24 bg-gradient-to-r from-orange-400 to-red-400 rounded-full opacity-20 blur-xl"
                animate={{
                  scale: [1, 1.3, 1],
                  opacity: [0.2, 0.4, 0.2],
                }}
                transition={{
                  duration: 5,
                  repeat: Infinity,
                  ease: "easeInOut"
                }}
              />
              
              <motion.div
                className="absolute -bottom-8 -left-8 w-20 h-20 border-2 border-cyan-400 opacity-30"
                style={{ borderRadius: "30% 70% 70% 30% / 30% 30% 70% 70%" }}
                animate={{
                  rotate: [0, 180, 360],
                  borderRadius: [
                    "30% 70% 70% 30% / 30% 30% 70% 70%",
                    "70% 30% 30% 70% / 70% 70% 30% 30%",
                    "30% 70% 70% 30% / 30% 30% 70% 70%"
                  ]
                }}
                transition={{
                  duration: 12,
                  repeat: Infinity,
                  ease: "linear"
                }}
              />
            </motion.div>
          </div>
        </div>
      </div>

      {/* Cinematic Scroll Indicator */}
      <motion.div 
        className="absolute bottom-8 left-1/2 transform -translate-x-1/2"
        initial={{ opacity: 0, y: 30 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 1, delay: 3 }}
      >
        <motion.div
          className="flex flex-col items-center text-cyan-200/80"
          animate={{ y: [0, 12, 0] }}
          transition={{ duration: 2.5, repeat: Infinity, ease: "easeInOut" }}
        >
          <span className="text-sm mb-3 font-medium tracking-wider">Discover More</span>
          <motion.div
            className="w-6 h-10 border-2 border-cyan-400/50 rounded-full flex justify-center"
            whileHover={{ borderColor: 'rgba(6, 182, 212, 0.8)' }}
          >
            <motion.div
              className="w-1 h-3 bg-cyan-400 rounded-full mt-2"
              animate={{ y: [0, 12, 0] }}
              transition={{ duration: 1.5, repeat: Infinity, ease: "easeInOut" }}
            />
          </motion.div>
        </motion.div>
      </motion.div>
    </motion.section>
  );
};

export default Hero;
